Uri endpoint = new Uri("<endpoint>");
AzureKeyCredential credential = new AzureKeyCredential("<key>");
AgentsClient client = new AIProjectClient(endpoint, credential).GetAgentsClient(apiVersion: "2025-05-01-preview");

using RequestContent content = RequestContent.Create(new
{
    options = new
    {
        displayName = "<displayName>",
        configurationOptions = new object(),
    },
});
Response response = await client.CreateAgentAsync(content);

JsonElement result = JsonDocument.Parse(response.ContentStream).RootElement;
Console.WriteLine(result.GetProperty("agentId").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("displayName").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").ToString());

This sample shows how to call CreateAgentAsync with all request content and parse the result.

Uri endpoint = new Uri("<endpoint>");
AzureKeyCredential credential = new AzureKeyCredential("<key>");
AgentsClient client = new AIProjectClient(endpoint, credential).GetAgentsClient(apiVersion: "2025-05-01-preview");

using RequestContent content = RequestContent.Create(new
{
    options = new
    {
        displayName = "<displayName>",
        configurationOptions = new
        {
            agentModel = new
            {
                provider = "openai",
                options = new
                {
                    temperature = 123.45F,
                    topP = 123.45F,
                },
                id = "<id>",
                endpoint = "<endpoint>",
            },
            instructions = "<instructions>",
            tools = new object[]
            {
                new Dictionary<string, object>
                {
                    ["type"] = "Microsoft.BingGrounding",
                    ["connectionName"] = "<connectionName>",
                    ["options"] = new
                    {
                        type = "Function",
                        name = "<name>",
                        description = "<description>",
                        parameters = new
                        {
                            type = "<type>",
                            properties = new {},
                            required = new object[]
                            {
                                "<required>"
                            },
                        },
                        strict = true,
                    },
                    ["override"] = new
                    {
                        name = "<name>",
                        description = "<description>",
                    }
                }
            },
            toolChoice = new
            {
                type = "auto",
                toolNames = new object[]
                {
                    "<toolNames>"
                },
            },
        },
    },
});
Response response = await client.CreateAgentAsync(content);

JsonElement result = JsonDocument.Parse(response.ContentStream).RootElement;
Console.WriteLine(result.GetProperty("agentId").ToString());
Console.WriteLine(result.GetProperty("description").ToString());
Console.WriteLine(result.GetProperty("metadata").GetProperty("<key>").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("displayName").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("agentModel").GetProperty("id").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("agentModel").GetProperty("provider").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("agentModel").GetProperty("endpoint").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("agentModel").GetProperty("options").GetProperty("stringValue").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("agentModel").GetProperty("options").GetProperty("intValue").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("agentModel").GetProperty("options").GetProperty("floatValue").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("agentModel").GetProperty("options").GetProperty("boolValue").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("agentModel").GetProperty("options").GetProperty("nullValue").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("instructions").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("tools")[0].GetProperty("type").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("tools")[0].GetProperty("options").GetProperty("type").ToString());
Console.WriteLine(result.GetProperty("creationOptions").GetProperty("configurationOptions").GetProperty("tools")[0].GetProperty("override").GetProperty("name").ToString());
