This sample shows how to call StreamAsync with all parameters.

Uri endpoint = new Uri("<endpoint>");
AzureKeyCredential credential = new AzureKeyCredential("<key>");
AgentsClient client = new AIProjectClient(endpoint, credential).GetAgentsClient(apiVersion: "2025-05-01-preview");

AgentConfigurationOptions options = new AgentConfigurationOptions
{
    AgentModel = new OpenAIAgentModel("<id>", new JSONAny())
    {
        Endpoint = "<endpoint>",
    },
    Instructions = "<instructions>",
    Tools = {new BingGroundingToolDefinition("<connectionName>")
    {
        Options = new FunctionToolOptions("<name>")
        {
            Description = "<description>",
            Parameters = new JSONSchema("<type>", new Dictionary<string, JSONSchema>
            {
                ["key"] = default
            })
            {
                Required = {"<required>"},
            },
            Strict = true,
        },
        Override = new AgentToolDefinitionOverride
        {
            Name = "<name>",
            Description = "<description>",
            Parameters = default,
        },
    }},
    ToolChoice = new AutoToolChoiceBehavior(new string[] { "<toolNames>" }),
};
RunInputs inputs = new RunInputs(new ChatMessage[]
{
    new UserMessage(new AIContent[]
    {
        new TextContent("<text>")
        {
            Annotations = {new Annotations("<type>")
            {
                ToolCallId = "<ToolCallId>",
                JsonPath = "<jsonPath>",
                Url = "<url>",
                Start = 1234,
                End = 1234,
            }},
        }
    })
    {
        UserId = "<userId>",
        AgentId = "<agentId>",
        AgentRunId = "<agentRunId>",
        AuthorName = "<authorName>",
        CreatedAt = 1234L,
        CompletedAt = 1234L,
    }
})
{
    AgentId = "<agentId>",
    ThreadId = "<threadId>",
    Metadata =
    {
        ["key"] = "<metadata>"
    },
    Options = new RunOptions
    {
        TruncationStrategy = new TruncationStrategy(TruncationStrategyType.Auto)
        {
            LastMessages = 1234,
        },
    },
    UserId = "<userId>",
};
Response response = await client.StreamAsync(options, inputs);

