using RequestContent content = RequestContent.Create(new
{
    name = "<name>",
    agentModel = new
    {
        provider = "openai",
        options = new
        {
            temperature = 123.45F,
            topP = 123.45F,
        },
        id = "<id>",
        endpoint = "<endpoint>",
    },
    instructions = new object[]
    {
        new
        {
            role = "developer",
            content = new object[]
            {
                new
                {
                    type = "text",
