    {
      "id": "/subscriptions/fbd18c45-a1c8-444b-afd9-ae358bbd70a3/providers/Microsoft.Gallery.Admin/galleryItems/Microsoft.PublicIPAddress-ARM.1.0.2",
      "name": "Microsoft.PublicIPAddress-ARM.1.0.2",
