                    {
                        nodeFillType = "spread",
                    },
                    enableAutoScale = false,
                    enableInterNodeCommunication = true,
                    startTask = new
                    {
                        commandLine = "myprogram2.exe",
                        resourceFiles = new object[]
                        {
                            new
                            {
                                httpUrl = "http://mystorage1.blob.core.windows.net/scripts/myprogram2.exe?sas",
                                filePath = "myprogram2.exe",
                            }
