This sample shows how to call PatchAsync with all parameters and request content.

var credential = new DefaultAzureCredential();
var client = new PoolClient("<batchUrl>", credential);

var data = new {
    startTask = new {
        commandLine = "<commandLine>",
        containerSettings = new {
            containerRunOptions = "<containerRunOptions>",
            imageName = "<imageName>",
            registry = new {
                username = "<username>",
                password = "<password>",
                registryServer = "<registryServer>",
                identityReference = new {
                    resourceId = "<resourceId>",
                },
            },
            workingDirectory = "taskWorkingDirectory",
        },
        resourceFiles = new[] {
            new {
                autoStorageContainerName = "<autoStorageContainerName>",
                storageContainerUrl = "<storageContainerUrl>",
                httpUrl = "<httpUrl>",
                blobPrefix = "<blobPrefix>",
                filePath = "<filePath>",
                fileMode = "<fileMode>",
                identityReference = new {
                    resourceId = "<resourceId>",
                },
            }
        },
        environmentSettings = new[] {
            new {
                name = "<name>",
                value = "<value>",
            }
        },
        userIdentity = new {
            username = "<username>",
            autoUser = new {
                scope = "task",
                elevationLevel = "nonadmin",
            },
        },
        maxTaskRetryCount = 1234,
        waitForSuccess = true,
    },
    certificateReferences = new[] {
        new {
            thumbprint = "<thumbprint>",
            thumbprintAlgorithm = "<thumbprintAlgorithm>",
            storeLocation = "currentuser",
            storeName = "<storeName>",
            visibility = new[] {
                "starttask"
            },
        }
    },
    applicationPackageReferences = new[] {
        new {
            applicationId = "<applicationId>",
            version = "<version>",
        }
    },
    metadata = new[] {
        new {
            name = "<name>",
            value = "<value>",
        }
    },
    targetNodeCommunicationMode = "default",
};

Response response = await client.PatchAsync("<poolId>", RequestContent.Create(data), 1234, DateTimeOffset.UtcNow, null);
Console.WriteLine(response.Status);

