Console.WriteLine(result.GetProperty("properties").GetProperty("urn").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("kind").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("azureRbacActions")[0].ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("azureRbacDataActions")[0].ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("requiredAzureRoleDefinitionIds")[0].ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("runtimeProperties").GetProperty("kind").ToString());
Console.WriteLine(result.GetProperty("id").ToString());
Console.WriteLine(result.GetProperty("name").ToString());
Console.WriteLine(result.GetProperty("type").ToString());
