This sample shows how to call GetAllExperimentsAsync and parse the result.

TokenCredential credential = new DefaultAzureCredential();
ExperimentsClient client = new ExperimentsClient(Guid.Parse("73f411fe-4f43-4b4b-9cbd-6828d8f4cf9a"), credential);

await foreach (BinaryData item in client.GetAllExperimentsAsync(null, null, null))
{
    JsonElement result = JsonDocument.Parse(item.ToStream()).RootElement;
    Console.WriteLine(result.GetProperty("properties").GetProperty("steps")[0].GetProperty("name").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("steps")[0].GetProperty("branches")[0].GetProperty("name").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("steps")[0].GetProperty("branches")[0].GetProperty("actions")[0].GetProperty("name").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("steps")[0].GetProperty("branches")[0].GetProperty("actions")[0].GetProperty("type").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("selectors")[0].GetProperty("id").ToString());
    Console.WriteLine(result.GetProperty("properties").GetProperty("selectors")[0].GetProperty("type").ToString());
    Console.WriteLine(result.GetProperty("location").ToString());
}

