Console.WriteLine(result.GetProperty("properties").GetProperty("resources")[0].GetProperty("id").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("permission")[0].GetProperty("resourceId").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("permission")[0].GetProperty("missingPermissions")[0].ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("permission")[0].GetProperty("requiredPermissions")[0].ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("permission")[0].GetProperty("recommendedRoles")[0].ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("permission")[0].GetProperty("identity").GetProperty("objectId").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("permission")[0].GetProperty("identity").GetProperty("tenantId").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("resource")[0].GetProperty("resourceId").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("resource")[0].GetProperty("errorCode").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("resource")[0].GetProperty("errorMessage").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("errors").GetProperty("resource")[0].GetProperty("remediationUri").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("scenarioRunJson").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("scenarioRunSummary")[0].GetProperty("resources")[0].GetProperty("id").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("scenarioRunSummary")[0].GetProperty("actionUrn").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("scenarioRunSummary")[0].GetProperty("state").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("scenarioRunSummary")[0].GetProperty("startedAt").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("scenarioRunSummary")[0].GetProperty("completedAt").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("startedAt").ToString());
Console.WriteLine(result.GetProperty("properties").GetProperty("completedAt").ToString());
