await foreach (BinaryData item in client.GetAllWorkspacesAsync(null, null))
{
    JsonElement result = JsonDocument.Parse(item.ToStream()).RootElement;
    Console.WriteLine(result.GetProperty("properties").GetProperty("scopes")[0].ToString());
    Console.WriteLine(result.GetProperty("location").ToString());
}

