ConversationAnalysisClient client = new ConversationAnalysisClient(endpoint, credential);

using RequestContent content = RequestContent.Create(new
{
    displayName = "Redacting PII data from transcribed audio",
    analysisInput = new
    {
        conversations = new object[]
        {
            new
            {
                id = "1",
                language = "en",
                modality = "transcript",
                domain = "generic",
                conversationItems = new object[]
                {
                    new
                    {
                        participantId = "1",
                        id = "1",
                        text = "Good morning John Doe.",
                        itn = "good morning john doe",
                        maskedItn = "good morning john doe",
                        lexical = "good morning john doe",
                        wordLevelTimings = new object[]
                        {
                            new
                            {
                                word = "good",
                                offset = 390000L,
                                duration = 2700000L,
                            },
                            new
                            {
                                word = "morning",
                                offset = 4500000L,
                                duration = 920000L,
                            },
                            new
                            {
                                word = "john",
                                offset = 590000L,
                                duration = 2700000L,
                            },
                            new
                            {
                                word = "doe",
                                offset = 6300000L,
                                duration = 920000L,
                            }
                        },
                    }
                },
            }
        },
    },
    tasks = new object[]
    {
        new
        {
            taskName = "Conversation PII",
            kind = "ConversationalPIITask",
            parameters = new
            {
                modelVersion = "latest",
                piiCategories = new object[]
                {
                    "All"
                },
                redactionPolicy = new
                {
                    policyKind = "noMask",
                },
                redactionSource = "lexical",
                redactAudioTiming = true,
            },
        }
    },
});
