using RequestContent content = RequestContent.Create(new
{
    displayName = "Conversation Summarization Example",
    analysisInput = new
    {
        conversations = new object[]
        {
            new
            {
                id = "1",
                language = "en",
                modality = "transcript",
                conversationItems = new object[]
                {
                    new
                    {
                        participantId = "speaker 1",
                        id = "1",
                        text = "Let's get started.",
                        lexical = "",
                        itn = "",
                        maskedItn = "",
                        conversationItemLevelTiming = new
                        {
                            offset = 0L,
                            duration = 20000000L,
                        },
                    },
                    new
                    {
                        participantId = "speaker 2",
                        id = "2",
                        text = "OK. How many remaining bugs do we have now?",
                        lexical = "",
                        itn = "",
                        maskedItn = "",
                        conversationItemLevelTiming = new
                        {
                            offset = 20000000L,
                            duration = 50000000L,
                        },
                    },
                    new
                    {
                        participantId = "speaker 3",
                        id = "3",
                        text = "Only 3.",
                        lexical = "",
                        itn = "",
                        maskedItn = "",
                        conversationItemLevelTiming = new
                        {
                            offset = 50000000L,
                            duration = 60000000L,
                        },
                    }
                },
            }
        },
    },
    tasks = new object[]
    {
        new
        {
            taskName = "Conversation Summarization Task 1",
            kind = "ConversationalSummarizationTask",
            parameters = new
            {
                summaryAspects = new object[]
                {
                    "chapterTitle",
                    "narrative"
                },
            },
        }
    },
});
