#nullable disable

namespace Azure.AI.Language.Conversations
{
    /// <summary> Word level timing information generated by the speech to text API. The words in this object should have 1:1 correspondence with the &apos;lexical&apos; input to allow for audio redaction. </summary>
    public partial class WordLevelTiming : AudioTiming
    {
        /// <summary> Initializes a new instance of WordLevelTiming. </summary>
        public WordLevelTiming()
        {
        }

        /// <summary> Initializes a new instance of WordLevelTiming. </summary>
        /// <param name="offset"> Offset from start of speech audio, in ticks. 1 tick = 100 ns. </param>
        /// <param name="duration"> Duration of word articulation, in ticks. 1 tick = 100 ns. </param>
        /// <param name="word"> The word recognized. </param>
        internal WordLevelTiming(long? offset, long? duration, string word) : base(offset, duration)
        {
            Word = word;
        }

        /// <summary> The word recognized. </summary>
        public string Word { get; set; }
    }
}
