using RequestContent content = RequestContent.Create(new
{
    sourceLocation = new
    {
        location = "http://localhost:3000",
        prefix = "<prefix>",
        extensions = new object[]
        {
            "<extensions>"
        },
    },
    targetLocation = new
    {
        location = "http://localhost:3000",
        prefix = "<prefix>",
    },
    operation = "Redact",
    dataType = "Plaintext",
    redactionFormat = "<redactionFormat>",
});
