{
    messages = new List<object>()
    {
        new
        {
            role = "system",
            content = "<content>",
            name = "<name>",
            function_call = new
            {
                name = "<name>",
                arguments = "<arguments>",
            },
            context = new
            {
                messages = new List<object>()
                {
                    null
                },
            },
        }
    },
