                    },
                    new Dictionary<string, object>
                    {
                        ["$id"] = "5232",
                        ["glossaryType"] = "AtlasGlossaryTerm"
                    }
                }
            }
        }
    }),
    Facets = {new SearchFacetItem
    {
        Count = 10,
        Facet = "termStatus",
        Sort = new SearchFacetSort
        {
            Count = SearchSortOrder.Descend,
        },
    }, new SearchFacetItem
    {
        Count = 10,
        Facet = "termTemplate",
