    properties = new {
        description = "<description>",
        excludedSystemClassifications = new[] {
            "<String>"
        },
        includedCustomClassificationRuleNames = new[] {
            "<String>"
        },
    },
    kind = "AzureSubscription",
    scanRulesetType = "Custom",
};

