    {
      "properties": {
        "displayName": "Ensure that '.Net Framework' version is the latest, if used as a part of the Function App",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Periodically, newer versions are released for .Net Framework software either due to security flaws or to include additional functionality. Using the latest .Net framework version for web apps is recommended in order to to take advantage of security fixes, if any, and/or new functionalities of the latest version.",
        "metadata": {
          "category": "App Service"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Web/sites"
              },
              {
                "field": "kind",
                "like": "functionapp*"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Web/sites/config",
              "name": "web",
              "existenceCondition": {
                "field": "Microsoft.Web/sites/config/web.netFrameworkVersion",
                "in": [
                  "v3.0",
                  "v4.0"
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/10c1859c-e1a7-4df3-ab97-a487fa8059f6",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "10c1859c-e1a7-4df3-ab97-a487fa8059f6"
    },
    {
      "properties": {
        "displayName": "Custom subscription owner roles should not exist",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy ensures that no custom subscription owner roles exist.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Authorization/roleDefinitions"
              },
              {
                "field": "Microsoft.Authorization/roleDefinitions/type",
                "equals": "CustomRole"
              },
              {
                "anyOf": [
                  {
                    "not": {
                      "field": "Microsoft.Authorization/roleDefinitions/permissions[*].actions[*]",
                      "notEquals": "*"
                    }
                  },
                  {
                    "not": {
                      "field": "Microsoft.Authorization/roleDefinitions/permissions.actions[*]",
                      "notEquals": "*"
                    }
                  }
                ]
              },
              {
                "not": {
                  "field": "Microsoft.Authorization/roleDefinitions/assignableScopes[*]",
                  "notIn": [
                    "[concat(subscription().id,'/')]",
                    "[subscription().id]",
                    "/"
                  ]
                }
              },
              {
                "not": {
                  "field": "Microsoft.Authorization/roleDefinitions/assignableScopes[*]",
                  "notLike": "/providers/Microsoft.Management/*"
                }
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
