    },
    {
      "properties": {
        "displayName": "The Log Analytics agent should be installed on virtual machines",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy audits any Windows/Linux virtual machines if the Log Analytics agent is not installed.",
        "metadata": {
          "category": "Monitoring"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Compute/virtualMachines"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "Microsoft.EnterpriseCloud.Monitoring"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "in": [
                      "MicrosoftMonitoringAgent",
                      "OmsAgentForLinux"
                    ]
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/provisioningState",
                    "equals": "Succeeded"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/settings.workspaceId",
                    "exists": "true"
                  }
                ]
              }
            }
          }
        }
      },
