    {
      "properties": {
        "displayName": "[Deprecated]: Audit Web Applications that are not using latest supported Java Framework",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Use the latest supported Java version for the latest security classes. Using older classes and types can make your application vulnerable.",
        "metadata": {
          "category": "Security Center",
          "preview": true,
          "deprecated": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "microsoft.Web/sites"
              },
              {
                "anyOf": [
                  {
                    "field": "kind",
                    "equals": "app"
                  },
                  {
                    "field": "kind",
                    "equals": "WebApp"
                  },
                  {
                    "field": "kind",
                    "equals": "app,linux"
                  },
                  {
                    "field": "kind",
                    "equals": "app,linux,container"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
