              "australiasoutheast",
              "australiaeast",
              "brazilsouth",
              "canadacentral",
              "centralindia",
              "centralus",
              "eastasia",
              "eastus2euap",
              "eastus",
              "eastus2",
              "francecentral",
              "japaneast",
              "koreacentral",
              "northcentralus",
              "northeurope",
              "norwayeast",
              "southcentralus",
              "southeastasia",
              "switzerlandnorth",
              "uaenorth",
              "uksouth",
              "westcentralus",
              "westeurope",
              "westus",
              "westus2"
            ]
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
