      "id": "/providers/Microsoft.Authorization/policyDefinitions/11ba0508-58a8-44de-5f3a-9e05d80571da",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "11ba0508-58a8-44de-5f3a-9e05d80571da"
    },
    {
      "properties": {
        "displayName": "Function app slots should disable public network access",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Disabling public network access improves security by ensuring that the Function app is not exposed on the public internet. Creating private endpoints can limit exposure of a Function App. Learn more at: https://aka.ms/app-service-private-endpoint.",
        "metadata": {
          "version": "1.0.0",
          "category": "App Service"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled",
              "Deny"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Web/sites/slots"
              },
              {
                "field": "kind",
                "contains": "functionapp"
              },
              {
                "field": "kind",
                "notContains": "workflowapp"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Web/sites/slots/publicNetworkAccess",
                    "exists": "false"
                  },
                  {
                    "field": "Microsoft.Web/sites/slots/publicNetworkAccess",
                    "notEquals": "Disabled"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/11c82d0c-db9f-4d7b-97c5-f3f9aa957da2",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "11c82d0c-db9f-4d7b-97c5-f3f9aa957da2"
    },
    {
      "properties": {
        "displayName": "[Preview]: Recovery Services vaults should use private link",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Azure Private Link lets you connect your virtual network to Azure services without a public IP address at the source or destination. The Private Link platform handles the connectivity between the consumer and services over the Azure backbone network. By mapping private endpoints to Azure Recovery Services vaults, data leakage risks are reduced. Learn more about private links for Azure Site Recovery at: https://aka.ms/HybridScenarios-PrivateLink and https://aka.ms/AzureToAzure-PrivateLink.",
        "metadata": {
          "version": "1.0.0-preview",
          "category": "Site Recovery",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.RecoveryServices/vaults"
              },
              {
                "count": {
                  "field": "Microsoft.RecoveryServices/vaults/privateEndpointConnections[*]",
                  "where": {
                    "allOf": [
                      {
                        "field": "Microsoft.RecoveryServices/vaults/privateEndpointConnections[*].privateLinkServiceConnectionState.status",
                        "equals": "Approved"
                      },
                      {
                        "field": "Microsoft.RecoveryServices/vaults/privateEndpointConnections[*].provisioningState",
                        "equals": "Succeeded"
                      },
                      {
                        "field": "Microsoft.RecoveryServices/vaults/privateEndpointConnections[*].id",
                        "contains": "SiteRecovery"
                      }
                    ]
                  }
                },
                "less": 1
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/11e3da8c-1d68-4392-badd-0ff3c43ab5b0",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "11e3da8c-1d68-4392-badd-0ff3c43ab5b0"
