    {
      "properties": {
        "displayName": "Windows machines should meet requirements for 'System Audit Policies - Logon-Logoff'",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Windows machines should have the specified Group Policy settings in the category 'System Audit Policies - Logon-Logoff' for auditing IPSec, network policy, claims, account lockout, group membership, and logon/logoff events. This policy requires that the Guest Configuration prerequisites have been deployed to the policy assignment scope. For details, visit https://aka.ms/gcpol.",
        "metadata": {
          "category": "Guest Configuration",
          "version": "3.0.0",
          "requiredProviders": [
            "Microsoft.GuestConfiguration"
          ],
          "guestConfiguration": {
            "name": "AzureBaseline_SystemAuditPoliciesLogonLogoff",
            "version": "1.*",
            "configurationParameter": {
              "AuditGroupMembership": "Audit Group Membership;ExpectedValue"
            }
          }
        },
        "parameters": {
          "IncludeArcMachines": {
            "type": "String",
            "metadata": {
              "displayName": "Include Arc connected servers",
              "description": "By selecting this option, you agree to be charged monthly per Arc connected machine.",
              "portalReview": "true"
            },
            "allowedValues": [
              "true",
              "false"
            ],
            "defaultValue": "false"
          },
          "AuditGroupMembership": {
            "type": "String",
            "metadata": {
              "displayName": "Audit Group Membership",
              "description": "Specifies whether audit events are generated when group memberships are enumerated on the client computer."
            },
            "allowedValues": [
              "No Auditing",
              "Success",
              "Failure",
              "Success and Failure"
            ],
            "defaultValue": "Success"
          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of this policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "anyOf": [
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Compute/virtualMachines"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "in": [
                          "esri",
                          "incredibuild",
                          "MicrosoftDynamicsAX",
                          "MicrosoftSharepoint",
                          "MicrosoftVisualStudio",
                          "MicrosoftWindowsDesktop",
                          "MicrosoftWindowsServerHPCPack"
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "MicrosoftWindowsServer"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "notLike": "2008*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "MicrosoftSQLServer"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "notLike": "SQL2008*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "microsoft-dsvm"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "dsvm-win*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "microsoft-ads"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "in": [
                              "standard-data-science-vm",
                              "windows-data-science-vm"
                            ]
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "batch"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "equals": "rendering-windows2016"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "center-for-internet-security-inc"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "cis-windows-server-201*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "pivotal"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "bosh-windows-server*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "cloud-infrastructure-services"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "ad*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Compute/virtualMachines/osProfile.windowsConfiguration",
                                "exists": "true"
                              },
                              {
                                "field": "Microsoft.Compute/virtualMachines/storageProfile.osDisk.osType",
                                "like": "Windows*"
                              }
                            ]
                          },
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Compute/imageSKU",
                                "exists": "false"
                              },
                              {
                                "allOf": [
                                  {
                                    "field": "Microsoft.Compute/imageSKU",
                                    "notLike": "2008*"
                                  },
                                  {
                                    "field": "Microsoft.Compute/imageOffer",
                                    "notLike": "SQL2008*"
                                  }
                                ]
                              }
                            ]
                          }
                        ]
                      }
                    ]
                  }
                ]
              },
              {
                "allOf": [
                  {
                    "value": "[parameters('IncludeArcMachines')]",
                    "equals": "true"
                  },
                  {
                    "anyOf": [
                      {
                        "allOf": [
                          {
                            "field": "type",
                            "equals": "Microsoft.HybridCompute/machines"
                          },
                          {
                            "field": "Microsoft.HybridCompute/imageOffer",
                            "like": "windows*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "type",
                            "equals": "Microsoft.ConnectedVMwarevSphere/virtualMachines"
                          },
                          {
                            "field": "Microsoft.ConnectedVMwarevSphere/virtualMachines/osProfile.osType",
                            "like": "windows*"
                          }
                        ]
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.GuestConfiguration/guestConfigurationAssignments",
              "name": "AzureBaseline_SystemAuditPoliciesLogonLogoff",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                    "equals": "Compliant"
                  },
                  {
                    "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/parameterHash",
                    "equals": "[base64(concat('Audit Group Membership;ExpectedValue', '=', parameters('AuditGroupMembership')))]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/19be9779-c776-4dfa-8a15-a2fd5dc843d6",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "19be9779-c776-4dfa-8a15-a2fd5dc843d6"
    },
    {
      "properties": {
        "displayName": "[Preview]: Azure Key Vault Managed HSM should disable public network access",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Disable public network access for your Azure Key Vault Managed HSM so that it's not accessible over the public internet. This can reduce data leakage risks. Learn more at: https://docs.microsoft.com/azure/key-vault/managed-hsm/private-link#allow-trusted-services-to-access-managed-hsm.",
        "metadata": {
          "version": "1.0.0-preview",
          "category": "Key Vault",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.KeyVault/managedHSMs"
              },
              {
                "field": "Microsoft.KeyVault/managedHSMs/createMode",
                "notEquals": "recover"
              },
              {
                "field": "Microsoft.KeyVault/managedHSMs/networkAcls.defaultAction",
                "notEquals": "Deny"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
