        "metadata": {
          "version": "1.0.0",
          "category": "App Service"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled",
              "Deny"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Web/sites"
              },
              {
                "field": "kind",
                "notContains": "functionapp"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Web/sites/publicNetworkAccess",
                    "exists": "false"
                  },
                  {
                    "field": "Microsoft.Web/sites/publicNetworkAccess",
                    "notEquals": "Disabled"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
