    {
      "properties": {
        "displayName": "Application Insights components should block log ingestion and querying from public networks",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Improve Application Insights security by blocking log ingestion and querying from public networks. Only private-link connected networks will be able to ingest and query logs of this component. Learn more at https://aka.ms/AzMonPrivateLink#configure-application-insights.",
        "metadata": {
          "version": "1.1.0",
          "category": "Monitoring"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "The effect determines what happens when the policy rule is evaluated to match"
            },
            "allowedValues": [
              "audit",
              "Audit",
              "deny",
              "Deny",
              "disabled",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Insights/components"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Insights/components/publicNetworkAccessForIngestion",
                    "notEquals": "disabled"
                  },
                  {
                    "field": "Microsoft.Insights/components/publicNetworkAccessForQuery",
                    "notEquals": "disabled"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1bc02227-0cb6-4e11-8f53-eb0b22eab7e8",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1bc02227-0cb6-4e11-8f53-eb0b22eab7e8"
    },
    {
      "properties": {
        "displayName": "[Deprecated]: Ensure that 'PHP version' is the latest, if used as a part of the API app",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Periodically, newer versions are released for PHP software either due to security flaws or to include additional functionality. Using the latest PHP version for API apps is recommended in order to take advantage of security fixes, if any, and/or new functionalities of the latest version. We recommend all customers who are still using API Apps to implement the built-in policy called 'App Service apps that use PHP should use the latest 'PHP version'', which is scoped to include API apps.",
        "metadata": {
          "version": "2.1.0-deprecated",
          "category": "App Service",
          "deprecated": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "PHPLatestVersion": {
            "type": "String",
            "metadata": {
              "displayName": "Latest PHP version",
              "description": "Latest supported PHP version for App Services"
            },
            "defaultValue": "7.4"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Web/sites"
              },
              {
                "field": "kind",
                "like": "*api"
              },
              {
                "field": "kind",
                "contains": "linux"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Web/sites/config",
              "name": "web",
              "existenceCondition": {
                "anyOf": [
                  {
                    "field": "Microsoft.Web/sites/config/web.linuxFxVersion",
                    "notContains": "PHP"
                  },
                  {
                    "field": "Microsoft.Web/sites/config/web.linuxFxVersion",
                    "equals": "[concat('PHP|', parameters('PHPLatestVersion'))]"
                  }
                ]
              }
            }
          }
        }
      },
