                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "Microsoft.Compute/virtualMachines/securityProfile.securityType",
                "equals": "TrustedLaunch"
              },
              {
                "field": "Microsoft.Compute/virtualMachines/securityProfile.uefiSettings",
