    {
      "properties": {
        "displayName": "Configure Windows Virtual Machines to be associated with a Data Collection Rule",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deploy Association to link Windows virtual machines to specified Data Collection Rule. The list of locations and OS images are updated over time as support is increased.",
        "metadata": {
          "version": "2.0.0",
          "category": "Monitoring"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy."
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          },
          "listOfWindowsImageIdToInclude": {
            "type": "Array",
            "metadata": {
              "displayName": "Optional: List of virtual machine images that have supported Windows OS to add to scope",
              "description": "Example values: '/subscriptions/<subscriptionId>/resourceGroups/YourResourceGroup/providers/Microsoft.Compute/images/ContosoStdImage'"
            },
            "defaultValue": []
          },
          "dcrResourceId": {
            "type": "String",
            "metadata": {
              "displayName": "Data Collection Rule Resource Id",
              "description": "Resource Id of the Data Collection Rule to be applied on the virtual machines in scope."
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "location",
                "in": [
                  "australiacentral",
                  "australiacentral2",
                  "australiaeast",
                  "australiasoutheast",
                  "brazilsouth",
                  "brazilsoutheast",
                  "canadacentral",
                  "canadaeast",
                  "centralindia",
                  "centralus",
                  "eastasia",
                  "eastus2euap",
                  "eastus",
                  "eastus2",
                  "francecentral",
                  "francesouth",
                  "germanywestcentral",
                  "japaneast",
                  "japanwest",
                  "jioindiawest",
                  "koreacentral",
                  "koreasouth",
                  "northcentralus",
                  "northeurope",
                  "norwayeast",
                  "norwaywest",
                  "southafricanorth",
                  "southcentralus",
                  "southeastasia",
                  "southindia",
                  "switzerlandnorth",
                  "switzerlandwest",
                  "uaenorth",
                  "uksouth",
                  "ukwest",
                  "westcentralus",
                  "westeurope",
                  "westindia",
                  "westus",
                  "westus2"
                ]
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Compute/imageId",
                    "in": "[parameters('listOfWindowsImageIdToInclude')]"
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "MicrosoftWindowsServer"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "WindowsServer"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "2008-R2-SP1*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "2012-*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "2016-*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "2019-*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "2022-*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "MicrosoftWindowsServer"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "WindowsServerSemiAnnual"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "in": [
                          "Datacenter-Core-1709-smalldisk",
                          "Datacenter-Core-1709-with-Containers-smalldisk",
                          "Datacenter-Core-1803-with-Containers-smalldisk",
                          "Datacenter-Core-1809-with-Containers-smalldisk"
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "MicrosoftWindowsServerHPCPack"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "WindowsServerHPCPack"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "MicrosoftSQLServer"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "*-WS2016"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "*-WS2016-BYOL"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "*-WS2012R2"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "*-WS2012R2-BYOL"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
