            },
            "defaultValue": 600
          },
          "repositorySyncInterval": {
            "type": "Integer",
            "metadata": {
              "displayName": "Repository Sync Interval (seconds)",
              "description": "The time period, in seconds, between attempted syncs of the Git repository to the cluster."
            },
