    {
      "properties": {
        "displayName": "Audit Linux machines that have the specified applications installed",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Requires that prerequisites are deployed to the policy assignment scope. For details, visit https://aka.ms/gcpol. Machines are non-compliant if the Chef InSpec resource indicates that one or more of the packages provided by the parameter are installed.",
        "metadata": {
          "category": "Guest Configuration",
          "version": "4.0.0",
          "requiredProviders": [
            "Microsoft.GuestConfiguration"
          ],
          "guestConfiguration": {
            "name": "not_installed_application_linux",
            "version": "1.*",
            "configurationParameter": {
              "ApplicationName": "[ChefInSpec]NotInstalledApplicationLinuxResource1;AttributesYmlContent"
            }
          }
        },
        "parameters": {
          "IncludeArcMachines": {
            "type": "String",
            "metadata": {
              "displayName": "Include Arc connected servers",
              "description": "By selecting this option, you agree to be charged monthly per Arc connected machine.",
              "portalReview": "true"
            },
            "allowedValues": [
              "true",
              "false"
            ],
            "defaultValue": "false"
          },
          "ApplicationName": {
            "type": "String",
            "metadata": {
              "displayName": "Application names",
              "description": "A semicolon-separated list of the names of the applications that should not be installed. e.g. 'python; powershell'"
            }
          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of this policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "anyOf": [
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Compute/virtualMachines"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "in": [
                          "microsoft-aks",
                          "qubole-inc",
                          "datastax",
                          "couchbase",
                          "scalegrid",
                          "checkpoint",
                          "paloaltonetworks",
                          "debian"
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "OpenLogic"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "CentOS*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "notLike": "6*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "Oracle"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "equals": "Oracle-Linux"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "notLike": "6*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "RedHat"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "in": [
                              "RHEL",
                              "RHEL-HA",
                              "RHEL-SAP",
                              "RHEL-SAP-APPS",
                              "RHEL-SAP-HA",
                              "RHEL-SAP-HANA"
                            ]
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "notLike": "6*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "RedHat"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "in": [
                              "osa",
                              "rhel-byos"
                            ]
                          }
