      "id": "/providers/Microsoft.Authorization/policyDefinitions/10c1859c-e1a7-4df3-ab97-a487fa8059f6",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "10c1859c-e1a7-4df3-ab97-a487fa8059f6"
    },
    {
      "properties": {
        "displayName": "[Preview]: Configure ChangeTracking Extension for Linux Arc machines",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Configure Linux Arc machines to automatically install the ChangeTracking Extension to enable File Integrity Monitoring(FIM) in Azure Security Center. FIM examines operating system files, Windows registries, application software, Linux system files, and more, for changes that might indicate an attack. The extension can be installed in virtual machines and locations supported by Azure Monitor Agent.",
        "metadata": {
          "version": "1.0.0-preview",
          "category": "Security Center",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          },
          "listOfApplicableLocations": {
            "type": "Array",
            "metadata": {
              "displayName": "Applicable Locations",
              "description": "The list of locations where the policy should be applied.",
              "strongType": "location"
            },
            "allowedValues": [
              "australiasoutheast",
              "australiaeast",
              "brazilsouth",
              "canadacentral",
              "centralindia",
              "centralus",
              "eastasia",
              "eastus2euap",
              "eastus",
              "eastus2",
              "francecentral",
              "japaneast",
              "koreacentral",
              "northcentralus",
              "northeurope",
              "norwayeast",
              "southcentralus",
              "southeastasia",
              "switzerlandnorth",
              "uaenorth",
              "uksouth",
              "westcentralus",
              "westeurope",
              "westus",
              "westus2"
            ],
            "defaultValue": [
              "australiasoutheast",
              "australiaeast",
              "brazilsouth",
              "canadacentral",
              "centralindia",
              "centralus",
              "eastasia",
              "eastus2euap",
              "eastus",
              "eastus2",
              "francecentral",
              "japaneast",
              "koreacentral",
              "northcentralus",
              "northeurope",
              "norwayeast",
              "southcentralus",
              "southeastasia",
              "switzerlandnorth",
              "uaenorth",
              "uksouth",
              "westcentralus",
              "westeurope",
              "westus",
              "westus2"
            ]
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.HybridCompute/machines"
              },
              {
                "field": "location",
                "in": "[parameters('listOfApplicableLocations')]"
              },
              {
                "field": "Microsoft.HybridCompute/imageOffer",
                "like": "linux*"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.HybridCompute/machines/extensions",
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/92aaf0da-9dab-42b6-94a3-d43ce8d16293"
              ],
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.HybridCompute/machines/extensions/type",
                    "equals": "ChangeTracking-Linux"
                  },
                  {
                    "field": "Microsoft.HybridCompute/machines/extensions/Publisher",
                    "equals": "Microsoft.Azure.ChangeTrackingAndInventory"
                  },
                  {
                    "field": "Microsoft.HybridCompute/machines/extensions/provisioningState",
                    "equals": "Succeeded"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
