    {
      "properties": {
        "displayName": "Transparent Data Encryption on SQL databases should be enabled",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Transparent data encryption should be enabled to protect data-at-rest and meet compliance requirements",
        "metadata": {
          "version": "2.0.0",
          "category": "SQL"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Sql/servers/databases"
              },
              {
                "field": "name",
                "notEquals": "master"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Sql/servers/databases/transparentDataEncryption",
              "name": "current",
              "existenceCondition": {
                "anyOf": [
                  {
                    "field": "Microsoft.Sql/transparentDataEncryption.status",
                    "equals": "enabled"
                  },
                  {
                    "field": "Microsoft.Sql/servers/databases/transparentDataEncryption/state",
                    "equals": "enabled"
                  }
                ]
              }
            }
          }
        }
      },
