    {
      "properties": {
        "displayName": "Key vaults should have soft delete enabled",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deleting a key vault without soft delete enabled permanently deletes all secrets, keys, and certificates stored in the key vault. Accidental deletion of a key vault can lead to permanent data loss. Soft delete allows you to recover an accidentally deleted key vault for a configurable retention period.",
        "metadata": {
          "version": "2.0.0",
          "category": "Key Vault"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.KeyVault/vaults"
              },
              {
                "not": {
                  "field": "Microsoft.KeyVault/vaults/createMode",
                  "equals": "recover"
                }
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.KeyVault/vaults/enableSoftDelete",
                    "exists": "false"
                  },
                  {
                    "field": "Microsoft.KeyVault/vaults/enableSoftDelete",
                    "equals": "false"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1e66c121-a66a-4b1f-9b83-0fd99bf0fc2d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1e66c121-a66a-4b1f-9b83-0fd99bf0fc2d"
    },
    {
      "properties": {
        "displayName": "Linux machines should have Log Analytics agent installed on Azure Arc",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Machines are non-compliant if Log Analytics agent is not installed on Azure Arc enabled Linux server.",
        "metadata": {
          "category": "Guest Configuration",
          "version": "1.1.0",
          "requiredProviders": [
            "Microsoft.GuestConfiguration"
          ],
          "guestConfiguration": {
            "name": "LinuxLogAnalyticsAgentInstalled",
            "version": "1.*"
          }
        },
        "parameters": {
          "IncludeArcMachines": {
            "type": "String",
            "metadata": {
              "displayName": "Include Arc connected servers",
              "description": "By selecting this option, you agree to be charged monthly per Arc connected machine.",
              "portalReview": "true"
            },
            "allowedValues": [
              "true",
              "false"
            ],
            "defaultValue": "false"
          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of this policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "value": "[parameters('IncludeArcMachines')]",
                "equals": "true"
              },
              {
                "field": "type",
                "equals": "Microsoft.HybridCompute/machines"
              },
              {
                "field": "Microsoft.HybridCompute/imageOffer",
                "like": "linux*"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.GuestConfiguration/guestConfigurationAssignments",
              "name": "LinuxLogAnalyticsAgentInstalled",
              "existenceCondition": {
                "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                "equals": "Compliant"
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1e7fed80-8321-4605-b42c-65fc300f23a3",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1e7fed80-8321-4605-b42c-65fc300f23a3"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy Microsoft Defender for Endpoint agent on Windows virtual machines",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deploys Microsoft Defender for Endpoint on applicable Windows VM images.",
        "metadata": {
          "category": "Security Center",
          "version": "1.0.0-preview",
          "preview": true
        },
        "parameters": {
          "PublishersToExclude": {
            "type": "Array",
            "metadata": {
              "displayName": "Optional: List Of Image Publishers to exclude",
              "description": "List of Windows image Publishers to exclude from MDE provision"
            },
            "defaultValue": []
          },
          "ImageIdsToExclude": {
            "type": "Array",
            "metadata": {
              "displayName": "Optional: List of virtual machine images to exclude",
              "description": "Example value: '/subscriptions/<subscriptionId>/resourceGroups/YourResourceGroup/providers/Microsoft.Compute/images/ContosoImage'"
            },
            "defaultValue": []
          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "Microsoft.Compute/virtualMachines/storageProfile.osDisk.osType",
                "like": "Windows*"
              },
              {
                "anyOf": [
                  {
                    "not": {
                      "field": "Microsoft.Compute/virtualMachines/imagePublisher",
                      "in": "[parameters('PublishersToExclude')]"
                    }
                  },
                  {
                    "value": "[empty(parameters('PublishersToExclude'))]",
                    "equals": "true"
                  }
                ]
              },
              {
                "anyOf": [
                  {
                    "not": {
                      "field": "Microsoft.Compute/virtualMachines/storageProfile.imageReference.id",
                      "in": "[parameters('ImageIdsToExclude')]"
                    }
                  },
                  {
                    "value": "[empty(parameters('ImageIdsToExclude'))]",
                    "equals": "true"
                  }
                ]
              },
              {
                "anyOf": [
                  {
                    "not": {
                      "anyOf": [
                        {
                          "field": "Microsoft.Compute/imageSKU",
                          "contains": "win7"
                        },
                        {
                          "field": "Microsoft.Compute/imageSKU",
                          "contains": "win8"
                        },
                        {
                          "field": "Microsoft.Compute/imageSKU",
                          "contains": "2008"
                        },
                        {
                          "field": "Microsoft.Compute/imageSKU",
                          "contains": "win10"
                        },
                        {
                          "field": "Microsoft.Compute/imageSKU",
                          "contains": "windows-10"
                        },
                        {
                          "field": "Microsoft.Compute/imageOffer",
                          "contains": "windows-10"
                        },
                        {
                          "field": "Microsoft.Compute/imagePublisher",
                          "equals": "MicrosoftWindowsDesktop"
                        },
                        {
                          "field": "Microsoft.Compute/imagePublisher",
                          "equals": "azureopenshift"
                        },
                        {
                          "field": "Microsoft.Compute/imagePublisher",
                          "equals": "AzureDatabricks"
                        }
                      ]
                    }
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "MicrosoftWindowsDesktop"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "contains": "evd"
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/fb1c8493-542b-48eb-b624-b4c8fea62acd"
              ],
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "name": "MDE.Windows",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "Microsoft.Azure.AzureDefenderForServers"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "equals": "MDE.Windows"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/provisioningState",
                    "equals": "Succeeded"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "parameters": {
                    "vmName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    },
                    "azureResourceId": {
                      "value": "[concat('/subscriptions/', subscription().subscriptionId, '/resourceGroups/', resourceGroup().name, '/providers/Microsoft.Compute/virtualMachines/',field('name'))]"
                    }
                  },
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "azureResourceId": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "apiVersion": "2020-06-01",
                        "name": "[concat(parameters('vmName'), '/MDE.Windows')]",
                        "type": "Microsoft.Compute/virtualMachines/extensions",
                        "location": "[parameters('location')]",
                        "properties": {
                          "autoUpgradeMinorVersion": true,
                          "enableAutomaticUpgrade": true,
                          "publisher": "Microsoft.Azure.AzureDefenderForServers",
                          "type": "MDE.Windows",
                          "typeHandlerVersion": "1.0",
                          "settings": {
                            "azureResourceId": "[parameters('azureResourceId')]",
                            "vNextEnabled": "true"
                          },
                          "protectedSettings": {
                            "defenderForEndpointOnboardingScript": "[reference(subscriptionResourceId('Microsoft.Security/mdeOnboardings', 'Windows'), '2021-10-01-preview', 'full').properties.onboardingPackageWindows]"
                          }
                        }
                      }
                    ]
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1ec9c2c2-6d64-656d-6465-3ec3309b8579",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1ec9c2c2-6d64-656d-6465-3ec3309b8579"
    },
    {
      "properties": {
        "displayName": "Azure API for FHIR should use private link",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Azure API for FHIR should have at least one approved private endpoint connection. Clients in a virtual network can securely access resources that have private endpoint connections through private links. For more information, visit: https://aka.ms/fhir-privatelink.",
        "metadata": {
          "version": "1.0.0",
          "category": "API for FHIR"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.HealthcareApis/services"
              },
              {
                "count": {
                  "field": "Microsoft.HealthcareApis/services/privateEndpointConnections[*]",
                  "where": {
                    "field": "Microsoft.HealthcareApis/services/privateEndpointConnections[*].privateLinkServiceConnectionState.status",
                    "equals": "Approved"
                  }
                },
                "less": 1
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1ee56206-5dd1-42ab-b02d-8aae8b1634ce",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1ee56206-5dd1-42ab-b02d-8aae8b1634ce"
    },
    {
      "properties": {
        "displayName": "[Preview]: Azure Security agent should be installed on your Linux Arc machines",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Install the Azure Security agent on your Linux Arc machines in order to monitor your machines for security configurations and vulnerabilities. Results of the assessments can seen and managed in Azure Security Center.",
        "metadata": {
          "category": "Security Center",
          "version": "1.0.0-preview",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.HybridCompute/machines"
              },
              {
                "field": "Microsoft.HybridCompute/imageOffer",
                "like": "linux*"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.HybridCompute/machines/extensions",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.HybridCompute/machines/extensions/type",
                    "equals": "AzureSecurityLinuxAgent"
                  },
                  {
                    "field": "Microsoft.HybridCompute/machines/extensions/Publisher",
                    "equals": "Microsoft.Azure.Security.Monitoring"
                  },
                  {
                    "field": "Microsoft.HybridCompute/machines/extensions/provisioningState",
                    "equals": "Succeeded"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1f300abb-f5a0-41c3-a163-91bd3ed35de7",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1f300abb-f5a0-41c3-a163-91bd3ed35de7"
    },
    {
      "properties": {
        "displayName": "An Azure Active Directory administrator should be provisioned for SQL servers",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit provisioning of an Azure Active Directory administrator for your SQL server to enable Azure AD authentication. Azure AD authentication enables simplified permission management and centralized identity management of database users and other Microsoft services",
        "metadata": {
          "version": "1.0.0",
          "category": "SQL"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Sql/servers/administrators"
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1f314764-cb73-4fc9-b863-8eca98ac36e9",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1f314764-cb73-4fc9-b863-8eca98ac36e9"
    },
    {
      "properties": {
        "displayName": "Azure Monitor Logs clusters should be encrypted with customer-managed key",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Create Azure Monitor logs cluster with customer-managed keys encryption. By default, the log data is encrypted with service-managed keys, but customer-managed keys are commonly required to meet regulatory compliance. Customer-managed key in Azure Monitor gives you more control over the access to you data, see https://docs.microsoft.com/azure/azure-monitor/platform/customer-managed-keys.",
        "metadata": {
          "version": "1.0.0",
          "category": "Monitoring"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "The effect determines what happens when the policy rule is evaluated to match"
            },
            "allowedValues": [
              "audit",
              "deny",
              "disabled"
            ],
            "defaultValue": "audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.OperationalInsights/clusters"
              },
              {
                "anyOf": [
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.OperationalInsights/clusters/keyVaultProperties.keyVaultUri",
                        "equals": ""
                      },
                      {
                        "field": "Microsoft.OperationalInsights/clusters/keyVaultProperties.keyVaultUri",
                        "exists": "false"
                      }
                    ]
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.OperationalInsights/clusters/keyVaultProperties.keyName",
                        "equals": ""
                      },
                      {
                        "field": "Microsoft.OperationalInsights/clusters/keyVaultProperties.keyName",
                        "exists": "false"
                      }
                    ]
                  },
                  {
                    "not": {
                      "field": "Microsoft.OperationalInsights/clusters/keyVaultProperties.keyVersion",
                      "exists": "true"
                    }
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1f68a601-6e6d-4e42-babf-3f643a047ea2",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1f68a601-6e6d-4e42-babf-3f643a047ea2"
    },
    {
      "properties": {
        "displayName": "Deploy Diagnostic Settings for Event Hub to Log Analytics workspace",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deploys the diagnostic settings for Event Hub to stream to a regional Log Analytics workspace when any Event Hub which is missing this diagnostic settings is created or updated.",
        "metadata": {
          "version": "1.1.0",
          "category": "Monitoring"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          },
          "profileName": {
            "type": "String",
            "metadata": {
              "displayName": "Profile name",
              "description": "The diagnostic settings profile name"
            },
            "defaultValue": "setbypolicy_logAnalytics"
          },
          "logAnalytics": {
            "type": "String",
            "metadata": {
              "displayName": "Log Analytics workspace",
              "description": "Select Log Analytics workspace from dropdown list. If this workspace is outside of the scope of the assignment you must manually grant 'Log Analytics Contributor' permissions (or similar) to the policy assignment's principal ID.",
              "strongType": "omsWorkspace",
              "assignPermissions": true
            }
          },
          "metricsEnabled": {
            "type": "String",
            "metadata": {
              "displayName": "Enable metrics",
              "description": "Whether to enable metrics stream to the Log Analytics workspace - True or False"
            },
            "allowedValues": [
              "True",
              "False"
            ],
            "defaultValue": "False"
          },
          "logsEnabled": {
            "type": "String",
            "metadata": {
              "displayName": "Enable logs",
              "description": "Whether to enable logs stream to the Log Analytics workspace - True or False"
            },
            "allowedValues": [
              "True",
              "False"
            ],
            "defaultValue": "True"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.EventHub/namespaces"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "name": "[parameters('profileName')]",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                    "equals": "[parameters('logsEnabled')]"
                  },
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/metrics.enabled",
                    "equals": "[parameters('metricsEnabled')]"
                  }
                ]
              },
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/749f88d5-cbae-40b8-bcfc-e573ddc772fa",
                "/providers/microsoft.authorization/roleDefinitions/92aaf0da-9dab-42b6-94a3-d43ce8d16293"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "resourceName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "logAnalytics": {
                        "type": "string"
                      },
                      "metricsEnabled": {
                        "type": "string"
                      },
                      "logsEnabled": {
                        "type": "string"
                      },
                      "profileName": {
                        "type": "string"
                      }
                    },
                    "variables": {},
                    "resources": [
                      {
                        "type": "Microsoft.EventHub/namespaces/providers/diagnosticSettings",
                        "apiVersion": "2017-05-01-preview",
                        "name": "[concat(parameters('resourceName'), '/', 'Microsoft.Insights/', parameters('profileName'))]",
                        "location": "[parameters('location')]",
                        "dependsOn": [],
                        "properties": {
                          "workspaceId": "[parameters('logAnalytics')]",
                          "metrics": [
                            {
                              "category": "AllMetrics",
                              "enabled": "[parameters('metricsEnabled')]",
                              "retentionPolicy": {
                                "enabled": false,
                                "days": 0
                              }
                            }
                          ],
                          "logs": [
                            {
                              "category": "ArchiveLogs",
                              "enabled": true,
                              "retentionPolicy": {
                                "enabled": false,
                                "days": 0
                              }
                            },
                            {
                              "category": "OperationalLogs",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "AutoScaleLogs",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "KafkaCoordinatorLogs",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "KafkaUserErrorLogs",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "EventHubVNetConnectionEvent",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "CustomerManagedKeyUserLogs",
                              "enabled": "[parameters('logsEnabled')]"
                            }
                          ]
                        }
                      }
                    ],
                    "outputs": {}
                  },
                  "parameters": {
                    "location": {
                      "value": "[field('location')]"
                    },
                    "resourceName": {
                      "value": "[field('name')]"
                    },
                    "logAnalytics": {
                      "value": "[parameters('logAnalytics')]"
                    },
                    "metricsEnabled": {
                      "value": "[parameters('metricsEnabled')]"
                    },
                    "logsEnabled": {
                      "value": "[parameters('logsEnabled')]"
                    },
                    "profileName": {
                      "value": "[parameters('profileName')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1f6e93e8-6b31-41b1-83f6-36e449a42579",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1f6e93e8-6b31-41b1-83f6-36e449a42579"
    },
    {
      "properties": {
        "displayName": "Configure Azure Defender for Key Vaults to be enabled",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Azure Defender for Key Vault provides an additional layer of protection and security intelligence by detecting unusual and potentially harmful attempts to access or exploit key vault accounts.",
        "metadata": {
          "version": "1.0.1",
          "category": "Security Center"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Resources/subscriptions"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/pricings",
              "name": "KeyVaults",
              "deploymentScope": "subscription",
