      "id": "/providers/Microsoft.Authorization/policyDefinitions/244efd75-0d92-453c-b9a3-7d73ca36ed52",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "244efd75-0d92-453c-b9a3-7d73ca36ed52"
    },
    {
      "properties": {
        "displayName": "Resource logs in Azure Kubernetes Service should be enabled",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Azure Kubernetes Service's resource logs can help recreate activity trails when investigating security incidents. Enable it to make sure the logs will exist when needed",
        "metadata": {
          "version": "1.0.0",
          "category": "Kubernetes"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "requiredRetentionDays": {
            "type": "String",
            "metadata": {
              "displayName": "Required retention (days)",
              "description": "The required resource logs retention (in days)"
            },
            "defaultValue": "365"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.ContainerService/managedClusters"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "existenceCondition": {
                "count": {
                  "field": "Microsoft.Insights/diagnosticSettings/logs[*]",
                  "where": {
                    "anyOf": [
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                            "equals": "true"
                          },
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days",
                                "equals": "0"
                              },
                              {
                                "value": "[padLeft(current('Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days'), 3, '0')]",
                                "greaterOrEquals": "[padLeft(parameters('requiredRetentionDays'), 3, '0')]"
                              }
                            ]
                          },
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                            "equals": "true"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                            "equals": "true"
                          },
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                                "notEquals": "true"
                              },
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/storageAccountId",
                                "exists": false
                              }
                            ]
                          }
                        ]
                      }
                    ]
                  }
                },
                "greaterOrEquals": 1
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/245fc9df-fa96-4414-9a0b-3738c2f7341c",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "245fc9df-fa96-4414-9a0b-3738c2f7341c"
    },
    {
      "properties": {
        "displayName": "Configure Azure Activity logs to stream to specified Log Analytics workspace",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Deploys the diagnostic settings for Azure Activity to stream subscriptions audit logs to a Log Analytics workspace to monitor subscription-level events",
        "metadata": {
          "version": "1.0.0",
          "category": "Monitoring"
        },
        "parameters": {
          "logAnalytics": {
            "type": "String",
            "metadata": {
              "displayName": "Primary Log Analytics workspace",
              "description": "If this workspace is outside of the scope of the assignment you must manually grant 'Log Analytics Contributor' permissions (or similar) to the policy assignment's principal ID.",
              "strongType": "omsWorkspace",
              "assignPermissions": true
            }
          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          },
          "logsEnabled": {
            "type": "String",
            "metadata": {
              "displayName": "Enable logs",
              "description": "Whether to enable logs stream to the Log Analytics workspace - True or False"
            },
            "allowedValues": [
              "True",
              "False"
            ],
            "defaultValue": "True"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Resources/subscriptions"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "deploymentScope": "Subscription",
              "existenceScope": "Subscription",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                    "equals": "[parameters('logsEnabled')]"
                  },
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/workspaceId",
                    "equals": "[parameters('logAnalytics')]"
                  }
                ]
              },
              "deployment": {
                "location": "northeurope",
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2018-05-01/subscriptionDeploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "logAnalytics": {
                        "type": "string"
                      },
                      "logsEnabled": {
                        "type": "string"
                      }
                    },
                    "variables": {},
                    "resources": [
                      {
                        "name": "subscriptionToLa",
                        "type": "Microsoft.Insights/diagnosticSettings",
                        "apiVersion": "2017-05-01-preview",
                        "location": "Global",
                        "properties": {
                          "workspaceId": "[parameters('logAnalytics')]",
                          "logs": [
                            {
                              "category": "Administrative",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Security",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "ServiceHealth",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Alert",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Recommendation",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Policy",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Autoscale",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "ResourceHealth",
                              "enabled": "[parameters('logsEnabled')]"
                            }
                          ]
                        }
                      }
                    ],
                    "outputs": {}
                  },
                  "parameters": {
                    "logAnalytics": {
                      "value": "[parameters('logAnalytics')]"
                    },
                    "logsEnabled": {
                      "value": "[parameters('logsEnabled')]"
                    }
                  }
                }
              },
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/749f88d5-cbae-40b8-bcfc-e573ddc772fa",
                "/providers/microsoft.authorization/roleDefinitions/92aaf0da-9dab-42b6-94a3-d43ce8d16293"
              ]
            }
          }
        }
      },
