      "id": "/providers/Microsoft.Authorization/policyDefinitions/30f52897-df47-4ca0-81a8-a3be3e8dd226",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "30f52897-df47-4ca0-81a8-a3be3e8dd226"
    },
    {
      "properties": {
        "displayName": "Audit Windows machines missing any of specified members in the Administrators group",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Requires that prerequisites are deployed to the policy assignment scope. For details, visit https://aka.ms/gcpol. Machines are non-compliant if the local Administrators group does not contain one or more members that are listed in the policy parameter.",
        "metadata": {
          "category": "Guest Configuration",
          "version": "2.0.0",
          "requiredProviders": [
            "Microsoft.GuestConfiguration"
          ],
          "guestConfiguration": {
            "name": "AdministratorsGroupMembersToInclude",
            "version": "1.*",
            "configurationParameter": {
              "MembersToInclude": "[LocalGroup]AdministratorsGroup;MembersToInclude"
            }
          }
        },
        "parameters": {
          "IncludeArcMachines": {
            "type": "String",
            "metadata": {
              "displayName": "Include Arc connected servers",
              "description": "By selecting this option, you agree to be charged monthly per Arc connected machine.",
              "portalReview": "true"
            },
            "allowedValues": [
              "true",
              "false"
            ],
            "defaultValue": "false"
          },
          "MembersToInclude": {
            "type": "String",
            "metadata": {
              "displayName": "Members to include",
              "description": "A semicolon-separated list of members that should be included in the Administrators local group. Ex: Administrator; myUser1; myUser2"
            }
          }
        },
        "policyRule": {
          "if": {
            "anyOf": [
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Compute/virtualMachines"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "in": [
                          "esri",
                          "incredibuild",
                          "MicrosoftDynamicsAX",
                          "MicrosoftSharepoint",
                          "MicrosoftVisualStudio",
                          "MicrosoftWindowsDesktop",
                          "MicrosoftWindowsServerHPCPack"
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "MicrosoftWindowsServer"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "notLike": "2008*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "MicrosoftSQLServer"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "notLike": "SQL2008*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "microsoft-dsvm"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "dsvm-win*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "microsoft-ads"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "in": [
                              "standard-data-science-vm",
                              "windows-data-science-vm"
                            ]
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "batch"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "equals": "rendering-windows2016"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "center-for-internet-security-inc"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "cis-windows-server-201*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "pivotal"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "bosh-windows-server*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Compute/imagePublisher",
                            "equals": "cloud-infrastructure-services"
                          },
                          {
                            "field": "Microsoft.Compute/imageOffer",
                            "like": "ad*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Compute/virtualMachines/osProfile.windowsConfiguration",
                                "exists": "true"
                              },
                              {
                                "field": "Microsoft.Compute/virtualMachines/storageProfile.osDisk.osType",
                                "like": "Windows*"
                              }
                            ]
                          },
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Compute/imageSKU",
                                "exists": "false"
                              },
                              {
                                "allOf": [
                                  {
                                    "field": "Microsoft.Compute/imageSKU",
                                    "notLike": "2008*"
                                  },
                                  {
                                    "field": "Microsoft.Compute/imageOffer",
                                    "notLike": "SQL2008*"
                                  }
                                ]
                              }
                            ]
                          }
                        ]
                      }
                    ]
                  }
                ]
              },
              {
                "allOf": [
                  {
                    "value": "[parameters('IncludeArcMachines')]",
                    "equals": "true"
                  },
                  {
                    "anyOf": [
                      {
                        "allOf": [
                          {
                            "field": "type",
                            "equals": "Microsoft.HybridCompute/machines"
                          },
                          {
                            "field": "Microsoft.HybridCompute/imageOffer",
                            "like": "windows*"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "type",
                            "equals": "Microsoft.ConnectedVMwarevSphere/virtualMachines"
                          },
                          {
                            "field": "Microsoft.ConnectedVMwarevSphere/virtualMachines/osProfile.osType",
                            "like": "windows*"
                          }
                        ]
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "auditIfNotExists",
            "details": {
              "type": "Microsoft.GuestConfiguration/guestConfigurationAssignments",
              "name": "AdministratorsGroupMembersToInclude",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                    "equals": "Compliant"
                  },
                  {
                    "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/parameterHash",
                    "equals": "[base64(concat('[LocalGroup]AdministratorsGroup;MembersToInclude', '=', parameters('MembersToInclude')))]"
                  }
                ]
              }
            }
          }
        }
      },
