                      "value": "[field('location')]"
                    },
                    "azureResourceId": {
                      "value": "[concat('/subscriptions/', subscription().subscriptionId, '/resourceGroups/', resourceGroup().name, '/providers/Microsoft.HybridCompute/machines/',field('name'))]"
