    {
      "properties": {
        "displayName": "[Preview]: Configure Arc machines to automatically create the Security Center pipeline for Azure Monitor Agent",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Configure Arc machines to automatically create the Azure Security Center pipeline for Azure Monitor Agent. Security Center collects events from the agent and uses them to provide security alerts and tailored hardening tasks (recommendations). Create a resource group and Log Analytics workspace in the same region as the machine to store audit records. Target virtual machines must be in a supported location.",
        "metadata": {
          "category": "Security Center",
          "version": "1.0.0-preview",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.HybridCompute/machines"
              },
              {
                "field": "location",
                "in": [
                  "australiacentral",
                  "australiaeast",
                  "australiasoutheast",
                  "brazilsouth",
                  "canadacentral",
                  "canadaeast",
                  "centralindia",
                  "centralus",
                  "eastasia",
                  "eastus2euap",
                  "eastus",
                  "eastus2",
                  "francecentral",
                  "germanywestcentral",
                  "japaneast",
                  "japanwest",
                  "jioindiawest",
                  "koreacentral",
                  "koreasouth",
                  "northcentralus",
                  "northeurope",
                  "norwayeast",
                  "southafricanorth",
                  "southcentralus",
                  "southeastasia",
                  "southindia",
                  "switzerlandnorth",
                  "uaenorth",
                  "uksouth",
                  "ukwest",
                  "westcentralus",
                  "westeurope",
                  "westindia",
                  "westus",
                  "westus2"
                ]
              }
            ]
          },
          "then": {
