    {
      "properties": {
        "displayName": "[Deprecated]: Audit Web Applications that are not using latest supported Python Framework",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Use the latest supported Python version for the latest security classes. Using older classes and types can make your application vulnerable.",
        "metadata": {
          "version": "1.0.0-deprecated",
          "category": "Security Center",
          "deprecated": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allof": [
              {
                "field": "type",
                "equals": "microsoft.Web/sites"
              },
              {
                "anyof": [
                  {
                    "field": "kind",
                    "equals": "app"
                  },
                  {
                    "field": "kind",
                    "equals": "WebApp"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "UseLatestPython",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "in": [
                  "OffByPolicy",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
