              "ApplicationName": {
                "value": "[parameters('ApplicationName')]"
              }
            }
          },
          {
            "policyDefinitionReferenceId": "Audit_NotInstalledApp",
            "policyDefinitionId": "/providers/Microsoft.Authorization/policyDefinitions/7e56b49b-5990-4159-a734-511ea19b731c"
          }
        ]
      },
      "id": "/providers/Microsoft.Authorization/policySetDefinitions/d7fff7ea-9d47-4952-b854-b7da261e48f2",
      "type": "Microsoft.Authorization/policySetDefinitions",
      "name": "d7fff7ea-9d47-4952-b854-b7da261e48f2"
    },
    {
      "properties": {
        "displayName": "[Preview]: Audit FedRAMP Moderate controls and deploy specific VM Extensions to support audit requirements",
        "policyType": "BuiltIn",
        "description": "This initiative includes audit and VM Extension deployment policies that address a subset of FedRAMP M controls. Additional policies will be added in upcoming releases. For more information, please visit https://aka.ms/fedrampm-blueprint.",
        "metadata": {
          "category": "Regulatory Compliance"
        },
        "parameters": {
          "logAnalyticsWorkspaceId": {
            "type": "String",
            "metadata": {
              "displayName": "Log Analytics Workspace Id that VMs should be configured for",
              "description": "This is the Id (GUID) of the Log Analytics Workspace that the VMs should be configured for."
            }
          },
          "listOfResourceTypes": {
            "type": "Array",
            "metadata": {
              "displayName": "List of resource types that should have diagnostic logs enabled"
            },
            "allowedValues": [
              "Microsoft.AnalysisServices/servers",
              "Microsoft.ApiManagement/service",
              "Microsoft.Network/applicationGateways",
              "Microsoft.Automation/automationAccounts",
              "Microsoft.ContainerInstance/containerGroups",
              "Microsoft.ContainerRegistry/registries",
              "Microsoft.ContainerService/managedClusters",
              "Microsoft.Batch/batchAccounts",
              "Microsoft.Cdn/profiles/endpoints",
              "Microsoft.CognitiveServices/accounts",
              "Microsoft.DocumentDB/databaseAccounts",
              "Microsoft.DataFactory/factories",
              "Microsoft.DataLakeAnalytics/accounts",
              "Microsoft.DataLakeStore/accounts",
              "Microsoft.EventGrid/eventSubscriptions",
              "Microsoft.EventGrid/topics",
              "Microsoft.EventHub/namespaces",
              "Microsoft.Network/expressRouteCircuits",
              "Microsoft.Network/azureFirewalls",
              "Microsoft.HDInsight/clusters",
              "Microsoft.Devices/IotHubs",
              "Microsoft.KeyVault/vaults",
              "Microsoft.Network/loadBalancers",
              "Microsoft.Logic/integrationAccounts",
