{
  "value": [
    {
      "properties": {
        "displayName": "Audit enabling of diagnostic logs in Azure Data Lake Store",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit enabling of logs and retain them up to a year. This enables you to recreate activity trails for investigation purposes when a security incident occurs or your network is compromised",
        "metadata": {
          "category": "Data Lake"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "requiredRetentionDays": {
            "type": "String",
            "metadata": {
              "displayName": "Required retention (days)",
              "description": "The required diagnostic logs retention in days"
            },
            "defaultValue": "365"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.DataLakeStore/accounts"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                    "equals": "true"
                  },
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days",
                    "equals": "[parameters('requiredRetentionDays')]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/057ef27e-665e-4328-8ea3-04b3122bd9fb",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "057ef27e-665e-4328-8ea3-04b3122bd9fb"
    },
    {
      "properties": {
        "displayName": "Audit SQL DB Level Audit Setting",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Audit DB level audit setting for SQL databases",
        "metadata": {
          "category": "SQL",
          "deprecated": true
        },
        "parameters": {
          "setting": {
            "type": "String",
            "metadata": {
              "displayName": "Audit Setting"
            },
            "allowedValues": [
              "enabled",
              "disabled"
            ]
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers/databases"
          },
          "then": {
            "effect": "AuditIfNotExists",
            "details": {
              "type": "Microsoft.Sql/servers/databases/auditingSettings",
              "name": "default",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Sql/auditingSettings.state",
                    "equals": "[parameters('setting')]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/06a78e20-9358-41c9-923c-fb736d382a12",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "06a78e20-9358-41c9-923c-fb736d382a12"
    },
    {
      "properties": {
        "displayName": "Audit VMs that do not use managed disks",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy audits VMs that do not use managed disks",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "anyOf": [
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Compute/virtualMachines"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/osDisk.uri",
                    "exists": "True"
                  }
                ]
              },
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Compute/VirtualMachineScaleSets"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Compute/VirtualMachineScaleSets/osDisk.vhdContainers",
                        "exists": "True"
                      },
                      {
                        "field": "Microsoft.Compute/VirtualMachineScaleSets/osdisk.imageUrl",
                        "exists": "True"
                      }
                    ]
