          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Sql/servers/administrators"
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1f314764-cb73-4fc9-b863-8eca98ac36e9",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1f314764-cb73-4fc9-b863-8eca98ac36e9"
    },
    {
      "properties": {
        "displayName": "[Preview]: Monitor unprotected web application in Azure Security Center",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Web applications without a Web Application Firewall protection will be monitored by Azure Security Center as recommendations",
        "metadata": {
          "category": "Security Center",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "in": [
              "Microsoft.Network/publicIPAddresses",
              "Microsoft.ClassicCompute/domainNames",
              "Microsoft.Web/hostingEnvironments"
