      "id": "/providers/Microsoft.Authorization/policyDefinitions/1d84d5fb-01f6-4d12-ba4f-4a26081d403d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1d84d5fb-01f6-4d12-ba4f-4a26081d403d"
    },
    {
      "properties": {
        "displayName": "Enforce tag and its value",
        "policyType": "BuiltIn",
        "description": "Enforces a required tag and its value. Does not apply to resource groups.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "tagName": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Name",
              "description": "Name of the tag, such as 'environment'"
            }
          },
          "tagValue": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Value",
              "description": "Value of the tag, such as 'production'"
            }
          }
        },
        "policyRule": {
          "if": {
            "not": {
              "field": "[concat('tags[', parameters('tagName'), ']')]",
              "equals": "[parameters('tagValue')]"
            }
          },
          "then": {
            "effect": "deny"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1e30110a-5ceb-460c-a204-c1c3969c6d62",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1e30110a-5ceb-460c-a204-c1c3969c6d62"
    },
    {
      "properties": {
        "displayName": "Audit provisioning of an Azure Active Directory administrator for SQL server",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit provisioning of an Azure Active Directory administrator for your SQL server to enable Azure AD authentication. Azure AD authentication enables simplified permission management and centralized identity management of database users and other Microsoft services",
        "metadata": {
          "category": "SQL"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Sql/servers/administrators"
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/1f314764-cb73-4fc9-b863-8eca98ac36e9",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "1f314764-cb73-4fc9-b863-8eca98ac36e9"
    },
    {
      "properties": {
        "displayName": "[Preview]: Monitor unprotected web application in Azure Security Center",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Web applications without a Web Application Firewall protection will be monitored by Azure Security Center as recommendations",
        "metadata": {
          "category": "Security Center",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "in": [
              "Microsoft.Network/publicIPAddresses",
              "Microsoft.ClassicCompute/domainNames",
              "Microsoft.Web/hostingEnvironments"
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "unprotectedWebApplication",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "equals": "Monitored"
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/201ea587-7c90-41c3-910f-c280ae01cfd6",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "201ea587-7c90-41c3-910f-c280ae01cfd6"
    },
    {
      "properties": {
        "displayName": "Audit enabling of only secure connections to your Redis Cache",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Audit enabling of only connections via SSL to Redis Cache. Use of secure connections ensures authentication between the server and the service and protects data in transit from network layer attacks such as man-in-the-middle, eavesdropping, and session-hijacking",
        "metadata": {
          "category": "Cache"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Cache/redis"
              },
              {
                "field": "Microsoft.Cache/Redis/enableNonSslPort",
                "equals": "true"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/22bee202-a82f-4305-9a2a-6d7f44d4dedb",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "22bee202-a82f-4305-9a2a-6d7f44d4dedb"
    },
    {
      "properties": {
        "displayName": "Audit configuration of metric alert rules on Batch accounts",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit configuration of metric alert rules on Batch account to enable the required metric",
        "metadata": {
          "category": "Batch"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "metricName": {
            "type": "String",
            "metadata": {
              "displayName": "Metric name",
              "description": "The metric name that an alert rule must be enabled on"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Batch/batchAccounts"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/alertRules",
              "existenceScope": "Subscription",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/alertRules/isEnabled",
                    "equals": "true"
                  },
                  {
                    "field": "Microsoft.Insights/alertRules/condition.dataSource.metricName",
                    "equals": "[parameters('metricName')]"
                  },
                  {
                    "field": "Microsoft.Insights/alertRules/condition.dataSource.resourceUri",
                    "equals": "[concat('/subscriptions/', subscription().subscriptionId, '/resourcegroups/', resourceGroup().name, '/providers/Microsoft.Batch/batchAccounts/', field('name'))]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/26ee67a2-f81a-4ba8-b9ce-8550bd5ee1a7",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "26ee67a2-f81a-4ba8-b9ce-8550bd5ee1a7"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy default Microsoft IaaSAntimalware extension for Windows Server",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy deploys a Microsoft IaaSAntimalware extension with a default configuraion when a VM is not configured with the antimalware extension.",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "Microsoft.Compute/imagePublisher",
                "equals": "MicrosoftWindowsServer"
              },
              {
                "field": "Microsoft.Compute/imageOffer",
                "equals": "WindowsServer"
              },
              {
                "field": "Microsoft.Compute/imageSKU",
                "in": [
                  "2008-R2-SP1",
                  "2008-R2-SP1-smalldisk",
                  "2012-Datacenter",
                  "2012-Datacenter-smalldisk",
                  "2012-R2-Datacenter",
                  "2012-R2-Datacenter-smalldisk",
                  "2016-Datacenter",
                  "2016-Datacenter-Server-Core",
                  "2016-Datacenter-Server-Core-smalldisk",
                  "2016-Datacenter-smalldisk",
                  "2016-Datacenter-with-Containers",
                  "2016-Datacenter-with-RDSH"
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "equals": "IaaSAntimalware"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "Microsoft.Azure.Security"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "ExclusionsPaths": {
                        "type": "string",
                        "defaultValue": "",
                        "metadata": {
                          "description": "Semicolon delimited list of file paths or locations to exclude from scanning"
                        }
                      },
                      "ExclusionsExtensions": {
                        "type": "string",
                        "defaultValue": "",
                        "metadata": {
                          "description": "Semicolon delimited list of file extensions to exclude from scanning"
                        }
                      },
                      "ExclusionsProcesses": {
                        "type": "string",
                        "defaultValue": "",
                        "metadata": {
                          "description": "Semicolon delimited list of process names to exclude from scanning"
                        }
                      },
                      "RealtimeProtectionEnabled": {
                        "type": "string",
                        "defaultValue": "true",
                        "metadata": {
                          "description": "Indicates whether or not real time protection is enabled (default is true)"
                        }
                      },
                      "ScheduledScanSettingsIsEnabled": {
                        "type": "string",
                        "defaultValue": "false",
                        "metadata": {
                          "description": "Indicates whether or not custom scheduled scan settings are enabled (default is false)"
                        }
                      },
                      "ScheduledScanSettingsScanType": {
                        "type": "string",
                        "defaultValue": "Quick",
                        "metadata": {
                          "description": "Indicates whether scheduled scan setting type is set to Quick or Full (default is Quick)"
                        }
                      },
                      "ScheduledScanSettingsDay": {
                        "type": "string",
                        "defaultValue": "7",
                        "metadata": {
                          "description": "Day of the week for scheduled scan (1-Sunday, 2-Monday, ..., 7-Saturday)"
                        }
                      },
                      "ScheduledScanSettingsTime": {
                        "type": "string",
                        "defaultValue": "120",
                        "metadata": {
                          "description": "When to perform the scheduled scan, measured in minutes from midnight (0-1440). For example: 0 = 12AM, 60 = 1AM, 120 = 2AM."
                        }
                      }
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('vmName'),'/IaaSAntimalware')]",
                        "type": "Microsoft.Compute/virtualMachines/extensions",
                        "location": "[parameters('location')]",
                        "apiVersion": "2017-12-01",
                        "properties": {
                          "publisher": "Microsoft.Azure.Security",
                          "type": "IaaSAntimalware",
                          "typeHandlerVersion": "1.3",
                          "autoUpgradeMinorVersion": true,
                          "settings": {
                            "AntimalwareEnabled": true,
                            "RealtimeProtectionEnabled": "[parameters('RealtimeProtectionEnabled')]",
                            "ScheduledScanSettings": {
                              "isEnabled": "[parameters('ScheduledScanSettingsIsEnabled')]",
                              "day": "[parameters('ScheduledScanSettingsDay')]",
                              "time": "[parameters('ScheduledScanSettingsTime')]",
                              "scanType": "[parameters('ScheduledScanSettingsScanType')]"
                            },
                            "Exclusions": {
                              "Extensions": "[parameters('ExclusionsExtensions')]",
                              "Paths": "[parameters('ExclusionsPaths')]",
                              "Processes": "[parameters('ExclusionsProcesses')]"
                            }
                          }
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "vmName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    },
                    "RealtimeProtectionEnabled": {
                      "value": "true"
                    },
                    "ScheduledScanSettingsIsEnabled": {
                      "value": "true"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/2835b622-407b-4114-9198-6f7064cbe0dc",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "2835b622-407b-4114-9198-6f7064cbe0dc"
    },
    {
      "properties": {
        "displayName": "Apply tag and its default value",
        "policyType": "BuiltIn",
        "description": "Applies a required tag and its default value if it is not specified by the user. Does not apply to resource groups.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "tagName": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Name",
              "description": "Name of the tag, such as 'environment'"
            }
          },
          "tagValue": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Value",
              "description": "Value of the tag, such as 'production'"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "[concat('tags[', parameters('tagName'), ']')]",
            "exists": "false"
          },
          "then": {
            "effect": "append",
            "details": [
              {
                "field": "[concat('tags[', parameters('tagName'), ']')]",
                "value": "[parameters('tagValue')]"
              }
            ]
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/2a0e14a6-b0a6-4fab-991a-187a4f81c498",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "2a0e14a6-b0a6-4fab-991a-187a4f81c498"
    },
    {
      "properties": {
        "displayName": "Audit unrestricted network access to storage accounts",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit unrestricted network access in your storage account firewall settings. Instead, configure network rules so only applications from allowed networks can access the storage account. To allow connections from specific internet or on-premise clients, access can be granted to traffic from specific Azure virtual networks or to public internet IP address ranges",
        "metadata": {
          "category": "Storage"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Storage/storageAccounts"
              },
              {
                "field": "Microsoft.Storage/storageAccounts/networkAcls.defaultAction",
                "equals": "Allow"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/34c877ad-507e-4c82-993e-3452a6e0ad3c",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "34c877ad-507e-4c82-993e-3452a6e0ad3c"
    },
    {
      "properties": {
        "displayName": "Audit enabling of diagnostic logs in Logic Apps",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit enabling of logs and retain them up to a year. This enables you to recreate activity trails for investigation purposes when a security incident occurs or your network is compromised",
        "metadata": {
          "category": "Logic Apps"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "requiredRetentionDays": {
            "type": "String",
            "metadata": {
              "displayName": "Required retention (days)",
              "description": "The required diagnostic logs retention in days"
            },
            "defaultValue": "365"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Logic/workflows"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                    "equals": "true"
                  },
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days",
                    "equals": "[parameters('requiredRetentionDays')]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/34f95f76-5386-4de7-b824-0d8478470c9d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "34f95f76-5386-4de7-b824-0d8478470c9d"
    },
    {
      "properties": {
        "displayName": "Audit enablement of encryption of Automation account variables",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "It is important to enable encryption of Automation account variable assets when storing sensitive data",
        "metadata": {
          "category": "Automation"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Automation/automationAccounts/variables"
              },
              {
                "field": "Microsoft.Automation/automationAccounts/variables/isEncrypted",
                "notEquals": "true"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/3657f5a0-770e-44a3-b44e-9431ba1e9735",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "3657f5a0-770e-44a3-b44e-9431ba1e9735"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy Threat Detection on SQL servers",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy ensures that Threat Detection is enabled on SQL Servers.",
        "metadata": {
          "category": "SQL",
          "preview": true
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers"
          },
          "then": {
            "effect": "DeployIfNotExists",
            "details": {
              "type": "Microsoft.Sql/servers/securityAlertPolicies",
              "name": "Default",
              "existenceCondition": {
                "field": "Microsoft.Sql/securityAlertPolicies.state",
                "equals": "Enabled"
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "serverName": {
                        "type": "string"
                      }
                    },
                    "variables": {},
                    "resources": [
                      {
                        "name": "[concat(parameters('serverName'), '/Default')]",
                        "type": "Microsoft.Sql/servers/securityAlertPolicies",
                        "apiVersion": "2017-03-01-preview",
                        "properties": {
                          "state": "Enabled",
                          "emailAccountAdmins": true
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "serverName": {
                      "value": "[field('name')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/36d49e87-48c4-4f2e-beed-ba4ed02b71f5",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "36d49e87-48c4-4f2e-beed-ba4ed02b71f5"
    },
    {
      "properties": {
        "displayName": "Audit use of classic storage accounts",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Use new Azure Resource Manager v2 for your storage accounts to provide security enhancements such as: stronger access control (RBAC), better auditing, Azure Resource Manager based deployment and governance, access to managed identities, access to key vault for secrets, Azure AD-based authentication and support for tags and resource groups for easier security management",
        "metadata": {
          "category": "Storage"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.classicStorage/storageAccounts"
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/37e0d2fe-28a5-43d6-a273-67d37d1f5606",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "37e0d2fe-28a5-43d6-a273-67d37d1f5606"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy default OMS VM Extension for Ubuntu VMs.",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy deploys OMS VM Extensions on Ubuntu VMs, and connects to the selected Log Analytics workspace",
        "metadata": {
          "category": "Compute"
        },
        "parameters": {
          "logAnalytics": {
            "type": "String",
            "metadata": {
              "displayName": "Log Analytics workspace",
              "description": "Select Log Analytics workspace from dropdown list",
              "strongType": "omsWorkspace"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "Microsoft.Compute/imagePublisher",
                "equals": "Canonical"
              },
              {
                "field": "Microsoft.Compute/imageOffer",
                "equals": "UbuntuServer"
              },
              {
                "field": "Microsoft.Compute/imageSKU",
                "in": [
                  "14.04.2-LTS",
                  "12.04.5-LTS"
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "equals": "OmsAgentForLinux"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "Microsoft.EnterpriseCloud.Monitoring"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "logAnalytics": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('vmName'),'/omsPolicy')]",
                        "type": "Microsoft.Compute/virtualMachines/extensions",
                        "location": "[parameters('location')]",
