          },
          "tagValue": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Value",
              "description": "Value of the tag, such as 'production'"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "[concat('tags[', parameters('tagName'), ']')]",
            "exists": "false"
          },
          "then": {
            "effect": "append",
            "details": [
              {
                "field": "[concat('tags[', parameters('tagName'), ']')]",
                "value": "[parameters('tagValue')]"
              }
            ]
          }
        }
      },
