                      }
                    },
                    "resources": [
                      {
                        "apiVersion": "2016-09-01",
                        "type": "Microsoft.Network/networkWatchers",
                        "name": "[concat('networkWatcher_', parameters('location'))]",
                        "location": "[parameters('location')]"
                      }
