            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "metricName": {
            "type": "String",
            "metadata": {
              "displayName": "Metric name",
              "description": "The metric name that an alert rule must be enabled on"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Batch/batchAccounts"
          },
          "then": {
