      "id": "/providers/Microsoft.Authorization/policyDefinitions/5df82f4f-773a-4a2d-97a2-422a806f1a55",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "5df82f4f-773a-4a2d-97a2-422a806f1a55"
    },
    {
      "properties": {
        "displayName": "[Deprecated]: Audit Web Applications that are not using latest supported .NET Framework",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Use the latest supported .NET Framework version for the latest security classes. Using older classes and types can make your application vulnerable.",
        "metadata": {
          "category": "Security Center",
          "preview": true,
          "deprecated": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "microsoft.Web/sites"
              },
              {
                "anyOf": [
                  {
                    "field": "kind",
                    "equals": "app"
                  },
                  {
                    "field": "kind",
                    "equals": "WebApp"
                  },
                  {
                    "field": "kind",
                    "equals": "app,linux"
                  },
                  {
                    "field": "kind",
                    "equals": "app,linux,container"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "UseLatestDotNet",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "in": [
                  "Monitored",
                  "NotApplicable",
                  "OffByPolicy",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/5e3315e0-a414-4efb-a4d2-c7bd2b0443d2",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "5e3315e0-a414-4efb-a4d2-c7bd2b0443d2"
    },
    {
      "properties": {
        "displayName": "Audit Windows VMs that do not have the specified applications installed",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy audits Windows virtual machines that do not have the specified applications installed. This policy should only be used along with its corresponding deploy policy in an initiative. For more information on Guest Configuration policies, please visit https://aka.ms/gcpol",
        "metadata": {
          "category": "Guest Configuration"
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.GuestConfiguration/guestConfigurationAssignments"
              },
              {
                "field": "name",
                "equals": "WhitelistedApplication"
              },
              {
                "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                "notEquals": "Compliant"
              }
            ]
          },
          "then": {
            "effect": "audit"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/5e393799-e3ca-4e43-a9a5-0ec4648a57d9",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "5e393799-e3ca-4e43-a9a5-0ec4648a57d9"
    },
    {
      "properties": {
        "displayName": "Allow resource creation only in India data centers",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Allows resource creation in the following locations only: West India, South India, Central India",
        "metadata": {
          "category": "General",
          "deprecated": true
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "not": {
              "field": "location",
              "in": [
                "westindia",
                "southindia",
                "centralindia"
              ]
            }
          },
          "then": {
            "effect": "Deny"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/5ee85ce5-e7eb-44d6-b4a2-32a24be1ca54",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "5ee85ce5-e7eb-44d6-b4a2-32a24be1ca54"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy Log Analytics Agent for Linux VM Scale Sets (VMSS)",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deploy Log Analytics Agent for Linux VM Scale Sets if the VM Image (OS) is in the list defined and the agent is not installed. Note: if your scale set upgradePolicy is set to Manual, you need to apply the extension to the all VMs in the set by calling upgrade on them. In CLI this would be az vmss update-instances.",
        "metadata": {
          "category": "Monitoring"
        },
        "parameters": {
          "logAnalytics": {
            "type": "String",
            "metadata": {
              "displayName": "Log Analytics workspace",
              "description": "Select Log Analytics workspace from dropdown list. If this workspace is outside of the scope of the assignment you must manually grant 'Log Analytics Contributor' permissions (or similar) to the policy assignment's principal ID.",
              "strongType": "omsWorkspace",
              "assignPermissions": true
            }
          },
          "listOfImageIdToInclude": {
            "type": "Array",
            "metadata": {
              "displayName": "Optional: List of VM images that have supported Linux OS to add to scope",
              "description": "Example value: '/subscriptions/<subscriptionId>/resourceGroups/YourResourceGroup/providers/Microsoft.Compute/images/ContosoStdImage'"
            },
            "defaultValue": []
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachineScaleSets"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Compute/imageId",
                    "in": "[parameters('listOfImageIdToInclude')]"
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "RedHat"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "RHEL",
                          "RHEL-SAP-HANA"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "6.*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "7*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "SUSE"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "SLES",
                          "SLES-HPC",
                          "SLES-HPC-Priority",
                          "SLES-SAP",
                          "SLES-SAP-BYOS",
                          "SLES-Priority",
                          "SLES-BYOS",
                          "SLES-SAPCAL",
                          "SLES-Standard"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "12*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Canonical"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "UbuntuServer"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "14.04*LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "16.04*LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "18.04*LTS"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Oracle"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "Oracle-Linux"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "6.*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "7.*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "OpenLogic"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "CentOS",
                          "Centos-LVM",
                          "CentOS-SRIOV"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "6.*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "7*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "cloudera"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "cloudera-centos-os"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "like": "7*"
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachineScaleSets/extensions",
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/92aaf0da-9dab-42b6-94a3-d43ce8d16293",
                "/providers/microsoft.authorization/roleDefinitions/9980e02c-c2be-4d73-94e8-173b1dc7cf3c"
              ],
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachineScaleSets/extensions/type",
                    "equals": "OmsAgentForLinux"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachineScaleSets/extensions/publisher",
                    "equals": "Microsoft.EnterpriseCloud.Monitoring"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "logAnalytics": {
                        "type": "string"
                      }
                    },
                    "variables": {
                      "vmExtensionName": "MMAExtension",
                      "vmExtensionPublisher": "Microsoft.EnterpriseCloud.Monitoring",
                      "vmExtensionType": "OmsAgentForLinux",
                      "vmExtensionTypeHandlerVersion": "1.7"
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('vmName'), '/', variables('vmExtensionName'))]",
                        "type": "Microsoft.Compute/virtualMachineScaleSets/extensions",
                        "location": "[parameters('location')]",
                        "apiVersion": "2018-06-01",
                        "properties": {
                          "publisher": "[variables('vmExtensionPublisher')]",
                          "type": "[variables('vmExtensionType')]",
                          "typeHandlerVersion": "[variables('vmExtensionTypeHandlerVersion')]",
                          "autoUpgradeMinorVersion": true,
                          "settings": {
                            "workspaceId": "[reference(parameters('logAnalytics'), '2015-03-20').customerId]",
                            "stopOnMultipleConnections": "true"
                          },
                          "protectedSettings": {
                            "workspaceKey": "[listKeys(parameters('logAnalytics'), '2015-03-20').primarySharedKey]"
                          }
                        }
                      }
                    ],
                    "outputs": {
                      "policy": {
                        "type": "string",
                        "value": "[concat('Enabled extension for: ', parameters('vmName'))]"
                      }
                    }
                  },
                  "parameters": {
                    "vmName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    },
                    "logAnalytics": {
                      "value": "[parameters('logAnalytics')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/5ee9e9ed-0b42-41b7-8c9c-3cfb2fbe2069",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "5ee9e9ed-0b42-41b7-8c9c-3cfb2fbe2069"
    },
    {
      "properties": {
        "displayName": "Audit external accounts with read permissions on a subscription",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "External accounts with read privileges should be removed from your subscription in order to prevent unmonitored access.",
        "metadata": {
          "category": "Security Center"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Resources/subscriptions"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "RemoveExternalAccountsWithReadPermissions",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "in": [
                  "NotApplicable",
                  "OffByPolicy",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/5f76cf89-fbf2-47fd-a3f4-b891fa780b60",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "5f76cf89-fbf2-47fd-a3f4-b891fa780b60"
    },
    {
      "properties": {
        "displayName": "Audit Windows web servers that are not using secure communication protocols",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy audits Windows web servers that are not using secure communication protocols (TLS 1.1 or TLS 1.2). This policy should only be used along with its corresponding deploy policy in an initiative. For more information on Guest Configuration policies, please visit https://aka.ms/gcpol",
        "metadata": {
          "category": "Guest Configuration"
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.GuestConfiguration/guestConfigurationAssignments"
              },
              {
                "field": "name",
                "equals": "AuditSecureProtocol"
              },
              {
                "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                "notEquals": "Compliant"
              }
            ]
          },
          "then": {
            "effect": "audit"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/60ffe3e2-4604-4460-8f22-0f1da058266c",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "60ffe3e2-4604-4460-8f22-0f1da058266c"
    },
    {
      "properties": {
        "displayName": "Deploy Advanced Data Security on SQL servers",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy enables Advanced Data Security on SQL Servers. This includes turning on Threat Detection and Vulnerability Assessment. It will automatically create a storage account in the same region and resource group as the SQL server to store scan results, with a 'sqlva' prefix.",
        "metadata": {
          "category": "SQL"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers"
          },
          "then": {
            "effect": "DeployIfNotExists",
            "details": {
              "type": "Microsoft.Sql/servers/securityAlertPolicies",
              "name": "Default",
              "existenceCondition": {
                "field": "Microsoft.Sql/securityAlertPolicies.state",
                "equals": "Enabled"
              },
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/056cd41c-7e88-42e1-933e-88ba6a50c9c3",
                "/providers/microsoft.authorization/roleDefinitions/17d1049b-9a84-46fb-8f53-869881c3d3ab"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "serverName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      }
                    },
                    "variables": {
                      "serverResourceGroupName": "[resourceGroup().name]",
                      "subscriptionId": "[subscription().subscriptionId]",
                      "uniqueStorage": "[uniqueString(variables('subscriptionId'), variables('serverResourceGroupName'), parameters('location'))]",
                      "storageName": "[tolower(concat('sqlva', variables('uniqueStorage')))]"
                    },
                    "resources": [
                      {
                        "type": "Microsoft.Storage/storageAccounts",
                        "name": "[variables('storageName')]",
                        "apiVersion": "2016-01-01",
                        "location": "[parameters('location')]",
                        "sku": {
                          "name": "Standard_LRS"
                        },
                        "kind": "Storage",
                        "properties": {}
                      },
                      {
                        "name": "[concat(parameters('serverName'), '/Default')]",
                        "type": "Microsoft.Sql/servers/securityAlertPolicies",
                        "apiVersion": "2017-03-01-preview",
                        "properties": {
                          "state": "Enabled",
                          "emailAccountAdmins": true
                        }
                      },
                      {
                        "name": "[concat(parameters('serverName'), '/Default')]",
                        "type": "Microsoft.Sql/servers/vulnerabilityAssessments",
                        "apiVersion": "2018-06-01-preview",
                        "properties": {
                          "storageContainerPath": "[concat(reference(resourceId('Microsoft.Storage/storageAccounts', variables('storageName'))).primaryEndpoints.blob, 'vulnerability-assessment')]",
                          "storageAccountAccessKey": "[listKeys(resourceId('Microsoft.Storage/storageAccounts', variables('storageName')), '2018-02-01').keys[0].value]",
                          "recurringScans": {
                            "isEnabled": true,
                            "emailSubscriptionAdmins": true,
                            "emails": []
                          }
                        },
                        "dependsOn": [
                          "[concat('Microsoft.Storage/storageAccounts/', variables('storageName'))]",
                          "[concat('Microsoft.Sql/servers/', parameters('serverName'), '/securityAlertPolicies/Default')]"
                        ]
                      }
                    ]
                  },
                  "parameters": {
                    "serverName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/6134c3db-786f-471e-87bc-8f479dc890f6",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "6134c3db-786f-471e-87bc-8f479dc890f6"
    },
    {
      "properties": {
        "displayName": "Audit the setting of ClusterProtectionLevel property to EncryptAndSign in Service Fabric",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Service Fabric provides three levels of protection (None, Sign and EncryptAndSign) for node-to-node communication using a primary cluster certificate. Set the protection level to ensure that all node-to-node messages are encrypted and digitally signed",
        "metadata": {
          "category": "Service Fabric"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.ServiceFabric/clusters"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.ServiceFabric/clusters/fabricSettings[*].name",
                    "notEquals": "Security"
                  },
                  {
                    "field": "Microsoft.ServiceFabric/clusters/fabricSettings[*].parameters[*].name",
                    "notEquals": "ClusterProtectionLevel"
                  },
                  {
                    "field": "Microsoft.ServiceFabric/clusters/fabricSettings[*].parameters[*].value",
                    "notEquals": "EncryptAndSign"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/617c02be-7f02-4efd-8836-3180d47b6c68",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "617c02be-7f02-4efd-8836-3180d47b6c68"
    },
    {
      "properties": {
        "displayName": "Audit missing blob encryption for storage accounts",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy audits storage accounts without blob encryption. It only applies to Microsoft.Storage resource types, not other storage providers. Possible network Just In Time access will be monitored by Azure Security Center as recommendations.",
        "metadata": {
          "category": "Security Center"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Storage/storageAccounts"
              },
              {
                "not": {
                  "field": "Microsoft.Storage/storageAccounts/enableBlobEncryption",
                  "equals": "True"
                }
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/655cb504-bcee-4362-bd4c-402e6aa38759",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "655cb504-bcee-4362-bd4c-402e6aa38759"
    },
    {
      "properties": {
        "displayName": "[Deprecated]: Audit IP restrictions configuration for a Function App",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "IP Restrictions allow you to define a list of IP addresses that are allowed to access your app. Use of IP Restrictions protects a Function app from common attacks.",
        "metadata": {
          "category": "Security Center",
          "preview": true,
          "deprecated": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "microsoft.Web/sites"
              },
              {
                "anyOf": [
                  {
                    "field": "kind",
                    "equals": "functionapp"
                  },
                  {
                    "field": "kind",
                    "equals": "functionapp,linux"
                  },
                  {
                    "field": "kind",
                    "equals": "functionapp,linux,container"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "ConfigureIPRestrictions",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "in": [
                  "Monitored",
                  "NotApplicable",
                  "OffByPolicy",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/664346d9-be92-43fb-a219-d595eeb76a90",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "664346d9-be92-43fb-a219-d595eeb76a90"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy requirements to audit Windows VMs on which the Log Analytics agent is not connected as expected",
