        "description": "Allows resource creation in the following locations only: Central US, East US, East US2, North Central US, South Central US, West US",
        "metadata": {
          "category": "General",
          "deprecated": true
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "not": {
              "field": "location",
              "in": [
                "centralus",
                "eastus",
                "eastus2",
                "northcentralus",
