          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/983211ba-f348-4758-983b-21fa29294869",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "983211ba-f348-4758-983b-21fa29294869"
    },
    {
      "properties": {
        "displayName": "Deploy Diagnostic Settings for Azure SQL Database to Event Hub",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deploys the diagnostic settings for Azure SQL Database to stream to a regional Event Hub on any Azure SQL Database which is missing this diagnostic settings is created or updated.",
        "metadata": {
          "category": "SQL"
        },
        "parameters": {
          "profileName": {
            "type": "String",
            "metadata": {
              "displayName": "Profile name",
              "description": "The diagnostic settings profile name"
            },
            "defaultValue": "setbypolicy"
          },
          "eventHubRuleId": {
            "type": "String",
            "metadata": {
              "displayName": "Event Hub Authorization Rule Id",
              "description": "The Event Hub authorization rule Id for Azure Diagnostics. The authorization rule needs to be at Event Hub namespace level. e.g. /subscriptions/{subscription Id}/resourceGroups/{resource group}/providers/Microsoft.EventHub/namespaces/{Event Hub namespace}/authorizationrules/{authorization rule}",
              "strongType": "Microsoft.EventHub/Namespaces/AuthorizationRules",
              "assignPermissions": true
            }
          },
          "metricsEnabled": {
            "type": "String",
            "metadata": {
              "displayName": "Enable metrics",
              "description": "Whether to enable metrics stream to the Event Hub - True or False"
            },
            "allowedValues": [
              "True",
              "False"
            ],
            "defaultValue": "False"
          },
          "logsEnabled": {
            "type": "String",
            "metadata": {
              "displayName": "Enable logs",
              "description": "Whether to enable logs stream to the Event Hub  - True or False"
            },
            "allowedValues": [
              "True",
              "False"
            ],
            "defaultValue": "True"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers/databases"
          },
          "then": {
            "effect": "DeployIfNotExists",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "name": "[parameters('profileName')]",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                    "equals": "[parameters('logsEnabled')]"
                  },
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/metrics.enabled",
                    "equals": "[parameters('metricsEnabled')]"
                  }
                ]
              },
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/b24988ac-6180-42a0-ab88-20f7382dd24c"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "fullName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "eventHubRuleId": {
                        "type": "string"
                      },
                      "metricsEnabled": {
                        "type": "string"
                      },
                      "logsEnabled": {
                        "type": "string"
                      },
                      "profileName": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "type": "Microsoft.Sql/servers/databases/providers/diagnosticSettings",
                        "apiVersion": "2017-05-01-preview",
                        "name": "[concat(parameters('fullName'), '/', 'Microsoft.Insights/', parameters('profileName'))]",
                        "location": "[parameters('location')]",
                        "dependsOn": [],
                        "properties": {
                          "eventHubAuthorizationRuleId": "[parameters('eventHubRuleId')]",
                          "metrics": [
                            {
                              "category": "AllMetrics",
                              "enabled": "[parameters('metricsEnabled')]",
                              "retentionPolicy": {
                                "enabled": false,
                                "days": 0
                              }
                            }
                          ],
                          "logs": [
                            {
                              "category": "QueryStoreRuntimeStatistics",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "QueryStoreWaitStatistics",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Errors",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "DatabaseWaitStatistics",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Blocks",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "SQLInsights",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Audit",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "SQLSecurityAuditEvents",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Timeouts",
                              "enabled": "[parameters('logsEnabled')]"
                            }
                          ]
                        }
                      }
                    ],
                    "outputs": {
                      "policy": {
                        "type": "string",
                        "value": "[concat('Enabled diagnostic settings for ', parameters('fullName'))]"
                      }
                    }
                  },
                  "parameters": {
                    "location": {
                      "value": "[field('location')]"
                    },
                    "fullName": {
                      "value": "[field('fullName')]"
                    },
                    "eventHubRuleId": {
                      "value": "[parameters('eventHubRuleId')]"
                    },
                    "metricsEnabled": {
                      "value": "[parameters('metricsEnabled')]"
                    },
                    "logsEnabled": {
                      "value": "[parameters('logsEnabled')]"
                    },
                    "profileName": {
                      "value": "[parameters('profileName')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/9a7c7a7d-49e5-4213-bea8-6a502b6272e0",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "9a7c7a7d-49e5-4213-bea8-6a502b6272e0"
    },
    {
      "properties": {
        "displayName": "[Deprecated]: Audit API Applications that are not using latest supported Java Framework",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Use the latest supported Java version for the latest security classes. Using older classes and types can make your application vulnerable.",
        "metadata": {
          "category": "Security Center",
          "preview": true,
          "deprecated": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "microsoft.Web/sites"
              },
              {
                "anyOf": [
                  {
                    "field": "kind",
                    "equals": "api"
                  },
                  {
                    "field": "kind",
                    "equals": "apiApp"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "UseLatestJava",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "in": [
                  "Monitored",
                  "NotApplicable",
                  "OffByPolicy",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/9bfe3727-0a17-471f-a2fe-eddd6b668745",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "9bfe3727-0a17-471f-a2fe-eddd6b668745"
    },
    {
      "properties": {
        "displayName": "Monitor permissive network access of Internet facing VMs in Azure Security Center",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Azure Security center has identified some of your Network Security Groups' inbound rules to be too permissive. Inbound rules should not allow access from 'Any' or 'Internet' ranges. This can potentially enable attackers to easily target your resources.",
        "metadata": {
          "category": "Security Center"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "in": [
              "Microsoft.Compute/virtualMachines",
              "Microsoft.ClassicCompute/virtualMachines"
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "unprotectedNetworkEndpoint",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "in": [
                  "Monitored",
                  "NotApplicable",
                  "OffByPolicy",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/9daedab3-fb2d-461e-b861-71790eead4f6",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "9daedab3-fb2d-461e-b861-71790eead4f6"
    },
    {
      "properties": {
        "displayName": "Append tag and its value from the resource group",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Appends the specified tag with its value from the resource group when any resource which is missing this tag is created or updated. Does not modify the tags of resources created before this policy was applied until those resources are changed.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "tagName": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Name",
              "description": "Name of the tag, such as 'environment'"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "[concat('tags[', parameters('tagName'), ']')]",
            "exists": "false"
          },
          "then": {
            "effect": "append",
            "details": [
              {
                "field": "[concat('tags[', parameters('tagName'), ']')]",
                "value": "[resourceGroup().tags[parameters('tagName')]]"
              }
            ]
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/9ea02ca2-71db-412d-8b00-7c7ca9fcd32d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "9ea02ca2-71db-412d-8b00-7c7ca9fcd32d"
    },
    {
      "properties": {
        "displayName": "Audit Windows VMs that are not set to the specified time zone",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy audits Windows virtual machines that are not set to the specified time zone. This policy should only be used along with its corresponding deploy policy in an initiative. For more information on Guest Configuration policies, please visit https://aka.ms/gcpol",
        "metadata": {
          "category": "Guest Configuration"
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.GuestConfiguration/guestConfigurationAssignments"
              },
              {
                "field": "name",
                "equals": "WindowsTimeZone"
              },
              {
                "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                "notEquals": "Compliant"
              }
            ]
          },
          "then": {
            "effect": "audit"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/9f658460-46b7-43af-8565-94fc0662be38",
      "type": "Microsoft.Authorization/policyDefinitions",
