            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.GuestConfiguration/guestConfigurationAssignments"
              },
              {
                "field": "name",
                "equals": "WindowsLogAnalyticsAgentConnection"
              },
              {
                "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                "notEquals": "Compliant"
              }
            ]
          },
          "then": {
            "effect": "audit"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/a030a57e-4639-4e8f-ade9-a92f33afe7ee",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "a030a57e-4639-4e8f-ade9-a92f33afe7ee"
    },
    {
      "properties": {
        "displayName": "Allowed resource types",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy enables you to specify the resource types that your organization can deploy. Only resource types that support 'tags' and 'location' will be affected by this policy. To restrict all resources please duplicate this policy and change the 'mode' to 'All'.",
        "metadata": {
          "category": "General"
        },
        "parameters": {
          "listOfResourceTypesAllowed": {
            "type": "Array",
            "metadata": {
              "description": "The list of resource types that can be deployed.",
              "displayName": "Allowed resource types",
              "strongType": "resourceTypes"
            }
          }
        },
        "policyRule": {
          "if": {
            "not": {
              "field": "type",
              "in": "[parameters('listOfResourceTypesAllowed')]"
            }
          },
          "then": {
            "effect": "deny"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/a08ec900-254a-4555-9bf5-e42af04b5c5c",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "a08ec900-254a-4555-9bf5-e42af04b5c5c"
    },
    {
      "properties": {
        "displayName": "Audit authorization rules on Service Bus namespaces",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Service Bus clients should not use a namespace level access policy that provides access to all queues and topics in a namespace. To align with the least privilege security model, you shoud create access policies at the entity level for queues and topics to provide access to only the specific entity",
        "metadata": {
          "category": "Service Bus"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.ServiceBus/namespaces/authorizationRules"
              },
              {
                "field": "name",
                "notEquals": "RootManageSharedAccessKey"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/a1817ec0-a368-432a-8057-8371e17ac6ee",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "a1817ec0-a368-432a-8057-8371e17ac6ee"
    },
    {
      "properties": {
        "displayName": "Audit Windows VMs that are not joined to the specified domain",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "This policy audits Windows virtual machines that are not joined to the specified domain. This policy should only be used along with its corresponding deploy policy in an initiative. For more information on Guest Configuration policies, please visit https://aka.ms/gcpol",
        "metadata": {
          "category": "Guest Configuration"
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.GuestConfiguration/guestConfigurationAssignments"
              },
              {
                "field": "name",
                "equals": "WindowsDomainMembership"
              },
              {
                "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                "notEquals": "Compliant"
              }
            ]
