            "Microsoft.GuestConfiguration"
          ]
        },
        "parameters": {
          "Members": {
            "type": "String",
            "metadata": {
              "displayName": "Members",
              "description": "A semicolon-separated list of all the expected members of the Administrators local group. Ex: Administrator; myUser1; myUser2"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
