            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "microsoft.Web/sites"
              },
              {
                "anyOf": [
                  {
                    "field": "kind",
                    "equals": "app"
                  },
                  {
                    "field": "kind",
                    "equals": "WebApp"
                  },
                  {
                    "field": "kind",
                    "equals": "app,linux"
                  },
                  {
